                        // SPLIT IMAGES INTO 2 COLUMNS
                        if(props.mmgRecords.length > 3) {
                        	$(".mmg-slides-outer", props.element).append('');
                            $(".mmg-slide:nth-child(n+4)", props.element).appendTo($(".mmg-slides", props.element).eq(1));
                        }

						// ADD IN ICONS FOR LINKS, ADD BACK TEXT FOR ACCESSIBILITY
                        $(".mmg-slide", props.element).each(function(index) {
                            if(!$(".mmg-description", this).length) {
                            	$(this).addClass("cs-no-hover");
                            } else {
                        		$(".mmg-description", this).wrapInner('
