                            setTimeout(function() {
                                var hasFocus = !! ($elem.find(':focus').length > 0);
                                if (! hasFocus) {
                                    $elem.removeClass("focus");
                                }
                            }, 10);
                        });
                    },
                    "onWindowResize": function(props) {
                  		$(".mmg-slide", props.element).each(function(index) {
                            $(".read-more", this).html(''+props.mmgRecords[index].linkText+'');
                            $(".watch-video", this).html(''+props.mmgRecords[index].videoLinkText+'');
