        "MMGPlugin": function() {
            // FOR SCOPE
            var _this = this;

            var mmg = eval("multimediaGallery" + $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").attr("data-pmi"));
            mmg.props.defaultGallery = false;

            $("#sw-content-container10 .ui-widget.app.multimedia-gallery:first").csMultimediaGallery({
                "imageWidth" : 1200,
                "imageHeight" : 600,
                "mobileDescriptionContainer": [768, 640, 480, 320], // [960, 768, 640, 480, 320]
                "galleryOverlay" : false,
                "playPauseControl" : true,
                "backNextControls" : true,
                "bullets" : false,
                "thumbnails" : false,
                "autoRotate" : true,
                "hoverPause" : true,
                "transitionType" : "fade",
                "transitionSpeed" : 1,
                "transitionDelay" : 2,
                "fullScreenRotator" : false,
                "fullScreenBreakpoints" : [960],
                "onImageLoad" : function(props) {
                	//WHOLE IMAGE CLICKABLE
                    if($(".mmg-slide.active .mmg-description-link.read-more", props.element).length && "false" == "true" ) {
                        var getPhotoLink = $(".mmg-slide.active .mmg-description-link.read-more", props.element).attr("href");
                        if(!$(".mmg-slide.active .mmg-image > a", props.element).length) {
                            $(".mmg-slides .mmg-slide.active img", props.element).wrap("");
                        }
                    }
                },
                "onTransitionStart" : function(props) {
                    // props.element, props.currentRecordIndex, props.currentGalleryIndex, props.nextRecordIndex, props.nextGalleryIndex, props.mmgRecords
                    if(props.mmgRecords.length > 1) {
                        //$(".hp-slideshow-background-image").eq(props.nextRecordIndex).addClass("next");                        
                        setTimeout(function() {
                            // UPDATE IMAGE CLASSES
                            //$(".hp-slideshow-background-image").eq(props.nextRecordIndex).toggleClass("next active").css("display", "");
                            //$(".hp-slideshow-background-image").eq(props.currentRecordIndex).toggleClass("active");
                        }, 1 * 1000);
                    }

                    if(!$("#hp-slideshow .mmg-description-outer .mmg-description-links span").length) {
                         $("#hp-slideshow .mmg-description-outer .mmg-description-links .mmg-description-link").wrapInner("");
                    }
                },
                "onTransitionEnd" : function(props) {
                	//WHOLE IMAGE CLICKABLE
                    if($(".mmg-slide.active .mmg-description-link.read-more", props.element).length && "false" == "true" ) {
                        var getPhotoLink = $(".mmg-slide.active .mmg-description-link.read-more", props.element).attr("href");
                        if(!$(".mmg-slide.active .mmg-image > a", props.element).length) {
                            $(".mmg-slides .mmg-slide.active img", props.element).wrap("");
                        }
                    }
                },
                "allLoaded" : function(props) {
                	if(props.mmgRecords.length > 1) {
                        $.each(props.mmgRecords, function(index, record) {
                            //$("#hp-slideshow-background").append('');
                        });
                    } else {
                    	//$("#hp-slideshow-background").append('');                    
                    }

                    //$(".hp-slideshow-background-image").eq(0).addClass("active");
                    $(".mmg-control").addClass("flex flex-justify-center flex-items-center").wrapAll('
');
                    $(".mmg-control.play-pause").insertAfter($(".mmg-control.back"));
                    $("#hp-slideshow .mmg-description-link").wrapInner("");
                },
                "onWindowResize": function(props) {
                    if(!$("#hp-slideshow .mmg-description-outer .mmg-description-links span").length) {
                         $("#hp-slideshow .mmg-description-outer .mmg-description-links .mmg-description-link").wrapInner("");
                    }                   
                }
            });
        },

        "ModEvents": function() {
            // FOR SCOPE
            var _this = this;

            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "no"
            });

			$(".ui-widget.app.upcomingevents .ui-article").each(function() {
            	var thisArticle = this;

				if (!$("h1.ui-article-title.sw-calendar-block-date", this).length && $(".ui-article-description", this).text() != "There are no upcoming events to display.") {
					var moveArticle = $(this).html();
					$(this).parent().prev("li").find(".upcoming-column.right").append(moveArticle);
					$(this).parent().remove();
				}

                if($(".ui-article-description", this).text() == "There are no upcoming events to display.") {
                	$(thisArticle).parent().addClass("no-events");
                }
			});

            // SWAP TIME AND TITLE SPANS
            $(".ui-widget.app.upcomingevents .ui-article-description").each(function() {
				$(".sw-calendar-block-time", this).appendTo($(this));
			});

            // WRAP VIEW CALENDAR INTO CUSTOM DIV
            $(".view-calendar-link").wrap('
');

            // ADD 0 TO EVENT DATES IF ONLY SINGLE DIGITS
            $(".ui-widget.app.upcomingevents .sw-calendar-block-date").each(function() {
                var text = $(this).text();
                var month = text.substring(0, 3);
             	var date = text.substring(3);

                if(date.length == 1) {
                	date = "0"+date;
                }

               	$(this).html(''+date+''+month+'');
            });

            // SET GLOBAL VARS FOR ALL HOMEPAGE UPCOMING EVENTS APPS
            $(".hp .upcomingevents").each(function() {
                var appID = $(this).closest("div[id*='pmi-']").attr("id").replace("pmi-", "");
                window["app" + appID] = $(this).html();
           	});

            // FUNCTIONALITY FOR EXPAND EVENTS BUTTON
            $(document).on("click keydown", ".cs-expand-events", function(e) {
                if(_this.A11yClick(e)) {
                    e.preventDefault();
                    if($(this).hasClass("active")) {
                        // HIDE ALL EVENTS EXCEPT FOR THE FIRST 6
                        $(this).parent().siblings(".cs-upcomingevents-articles:nth-of-type(2)").addClass("last-active");
                        $(this).html("Expand Events").attr("aria-label", "Expand Events").removeClass("active").parent().siblings(".cs-upcomingevents-articles:nth-of-type(n+3)").slideUp(500).attr("aria-hidden", "true");
                    } else {
                        // SHOW ALL EVENTS AND FOCUS THE FIRST ONE
                        $(this).html("Collapse Events").attr("aria-label", "Collapse Events").addClass("active").parent().siblings(".cs-upcomingevents-articles:nth-of-type(n+3)").slideDown(500).attr("aria-hidden", "false");
                        $(this).parent().siblings(".cs-upcomingevents-articles:nth-of-type(2)").removeClass("last-active");
                        $(this).parent().siblings(".cs-upcomingevents-articles:nth-of-type(3)").find("a:first").focus();
                    }
                }
            });
        },

       "DivideEvents": function() {
           // FOR SCOPE
           var _this = this;

           // ADD CUSTOM BUTTON IF MORE THEN 6 EVENTS
           $(".hp .upcomingevents").each(function() {
                var appID = $(this).closest("div[id*='pmi-']").attr("id").replace("pmi-", "");

                if(_this.GetBreakPoint() == "desktop" && $(".ui-articles > li", this).length > 3 && $(".upcoming-column-container", this).length) {
                	// RESET HTML STRUCTURE
                	$(this).html(window["app" + appID]);

                    // DIVIDE INTO ROWS OF 3 FOR ON HOMEPAGES
                    var children = $(".ui-articles > li", this);
                    for(var i = 0; i < children.length; i+=3) {
                        children.slice(i, i+3).wrapAll("");
                    }

                    $(".ui-articles > .ui-articles", this).wrap('
');

                    $(".cs-upcomingevents-articles:nth-of-type(2)", this).addClass("last-active");

                    if($(".ui-articles > li", this).length > 6) {
                    	$(".cs-upcomingevents-buttons", this).prepend('Expand Events</button');
					}

                    var content = $(".ui-articles", this).html();
                    $(".ui-articles", this).replaceWith(content);
                } else if((_this.GetBreakPoint() == 768 || _this.GetBreakPoint() == 640) && $(".ui-articles > li", this).length > 3 && $(".upcoming-column-container", this).length) {
                	// RESET HTML STRUCTURE 
                	$(this).html(window["app" + appID]);

                    // DIVIDE INTO ROWS OF 2 FOR ON HOMEPAGES
                    var children = $(".ui-articles > li", this);
                    for(var i = 0; i < children.length; i+=2) {
                        children.slice(i, i+2).wrapAll("");
                    }

                    $(".ui-articles > .ui-articles", this).wrap('
');

                    $(".cs-upcomingevents-articles:nth-of-type(3)", this).addClass("last-active");

                    if($(".ui-articles > li", this).length > 6) {
                    	$(".cs-upcomingevents-buttons", this).prepend('Expand Events</button');
					}

                    var content = $(".ui-articles", this).html();
                    $(".ui-articles", this).replaceWith(content);
                } else if (_this.GetBreakPoint() == 480 || _this.GetBreakPoint() == 320) {
                    // RESET HTML STRUCTURE
                	$(this).html(window["app" + appID]);               
                }
           });
        },

        "DivideShortcuts": function() {
            // FOR SCOPE
            var _this = this;

            $(".hp .siteshortcuts").each(function() {
                var appID = $(this).closest("div[id*='pmi-']").attr("id").replace("pmi-", "");

                if((_this.GetBreakPoint() == "desktop" || _this.GetBreakPoint() == 768) && ($(".site-shortcuts", this).length == 1 || $(".site-shortcuts", this).length == 2)) {
                    // RESET HTML STRUCTURE
                	$(this).html(window["app" + appID]);

                    $(".app-level-social-follow", this).appendTo($(".ui-widget-footer", this));

                    // DIVIDE INTO ROWS OF 3 FOR ON HOMEPAGES
                    var children = $(".site-shortcuts > li", this);
                    for(var i = 0; i < children.length; i+=3) {
                        children.slice(i, i+3).wrapAll("");
                    }

                    var content = $(".site-shortcuts", this).html();
                    $(".site-shortcuts", this).replaceWith(content);
                } else if(_this.GetBreakPoint() == 640 && ($(".site-shortcuts", this).length == 1 || $(".site-shortcuts", this).length == 3)) {
                    // RESET HTML STRUCTURE
                	$(this).html(window["app" + appID]);

                    $(".app-level-social-follow", this).appendTo($(".ui-widget-footer", this));

                    // DIVIDE THE ROWS IN HALF FOR ON HOMEPAGES
                    var children = $(".site-shortcuts > li", this);
                    var half = Math.round(children.length/2);
                    for(var i = 0; i < children.length; i+=half) {
                        children.slice(i, i+half).wrapAll("");
                    }

                    var content = $(".site-shortcuts", this).html();
                    $(".site-shortcuts", this).replaceWith(content);
                } else if (_this.GetBreakPoint() == 480 || _this.GetBreakPoint() == 320) {
                    // RESET HTML STRUCTURE
                	$(this).html(window["app" + appID]);               
                }
           });
        },

