        "Search": function() {
        	// FOR SCOPE
            var _this = this;

            // OPEN SEARCH DIALOG
            $(document).on("click keydown", "#gb-search-btn", function(e) {
                if(_this.A11yClick(e)) {
                	e.preventDefault();

                    if($(this).attr("aria-expanded") == "false") {
                    	$(this).attr({
                        	"aria-expanded": "true"
                        });
                        $("#gb-search").attr("aria-hidden", "false").show("slide", { direction: "right" }, 500);

                        setTimeout(function() {
                            $("#gb-search-input").focus();
                        }, 260);
                    } else {
                    	$(this).attr({
                        	"aria-expanded": "false"
                        });
                        $("#gb-search").attr("aria-hidden", "true").hide("slide", { direction: "right" }, 500);
                    }
                }
            });

			$("#gb-search-form").submit(function(e){
				e.preventDefault();
				if($("#gb-search-input").val() != "Search...") {
					window.location.href = "/site/Default.aspx?PageType=6&SiteID=4&SearchString=" + $("#gb-search-input").val();
                }
			});

			$("#gb-search-input").focus(function() {
				if($(this).val() == "What are you searching for?") {
					$(this).val("");
				}
			}).blur(function() {
				if($(this).val() == "") {
					$(this).val("What are you searching for?");
				}
			});
        },

        "A11yClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown") {
                if(event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter) {
                    return true;
                }
            } else {
                return false;
            }
        },

        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        }
    };

