        LoadCalendarSearchList();
    }  

    function SelectFilteredCalendars() {
        var arrValues = GetCookie('CalendarFilter').split('-');
        
        $.each(arrValues, function() {
            $('#calendarfilter-chk-calendar' + this).attr('checked', true);
        });
    }   

