	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				if(!$("#bb-footer-right #sw-mystart-mypasskey").length){
                	$("#sw-mystart-mypasskey").insertAfter(".ac-dropdown-container.user-options");
                }
			break;
			case "768":
				if(!$("#mobile-social-footer-inner #sw-mystart-mypasskey").length){
                	$("#sw-mystart-mypasskey").appendTo(".mobile-social-column.one");
                }
			break;
			case "640":
				if(!$("#mobile-social-footer-inner #sw-mystart-mypasskey").length){
                	$("#sw-mystart-mypasskey").appendTo(".mobile-social-column.one");
                }
			break;
			case "480":
				if(!$("#mobile-social-footer-inner #sw-mystart-mypasskey").length){
                	$("#sw-mystart-mypasskey").appendTo(".mobile-social-column.one");
                }
			break;
			case "320":
				if(!$("#mobile-social-footer-inner #sw-mystart-mypasskey").length){
                	$("#sw-mystart-mypasskey").appendTo(".mobile-social-column.one");
                }
			break;
		}
	}
