/* ------------------------------   CHANNEL DROPDOWN  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */
	function channelDropdown(){

    //WILL NEED TO LOOP THROUGH AN ARRAY OF CHANNEL ELEMENTS TO POPULATE IMAGES AND DESCRIPTIONS
		//CREATE AN ARRAY OF AVAILABLE CHANNEL DROPDOWN ITEMS,
        //ORDER IS CHANNEL IMAGE, CHANNEL DESCRIPTION
        var channelDropdownData = [['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///logos/DPSapple.png',''],
        ['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg',''],
        ['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg',''],
        ['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg',''],
        ['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg',''],
        ['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg',''],
        ['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg',''],
        ['/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg','']];	

		//CREATE A COUNTER
        var i = 0;
        $(".sw-channel-dropdown").each(function(){
			//GRAB CHANNEL NAME
			var channelName = $(this).parent().find("> a").text();

			//SETUP VARIABLES FROM THE ARRAY
            var channelImage = channelDropdownData[i][0];
			var channelDescription = channelDropdownData[i][1].trim();

			//WRAP DROPDOWN ITEMS IN AN LI
			$(this).wrapInner("
");

			//ADD AN LI FOR THE IMAGE AND DESCRIPTION TO THE CHANNEL DROPDOWN
			$(this).prepend("
"+
				""+channelName+""+
				""+
					""+
				""+
			""+
			"
"+
				""+channelName+""+
				"
"+channelDescription+""+
			"");

			//CHECK TO SEE IF AN IMAGE EXISTS, 
            //IF IT DOES, SHOW THE CHANNEL NAME ABOVE IT
            //AND ADD A CLASS TO THE DROPDOWN SO WE CAN DETERMINE WHICH COLUMN THE CHANNEL NAME SHOULD BE VISIBLE IN
			if(channelImage.indexOf("default-man") === -1){
				$(this).find(".channel-image").show();
				$(this).addClass("image-column");
			}

			//CHECK TO SEE IF A DESCRIPTION EXISTS,
            //IF IT DOES, SHOW IT
            //AND ADD A CLASS TO THE DROPDOWN SO WE CAN DETERMINE WHICH COLUMN THE CHANNEL NAME SHOULD BE VISIBLE IN
			if(channelDescription != ''){
				$(this).find(".channel-description").show();
				$(this).addClass("description-column");
			}

            //INCREMENT THE COUNTER
            i++;
		});
	}

