	function headlineCards(){

		$("#hp-content-top .headlines li").each(function(){
			var headlineBgImage = $(this).find(".img img").attr("src");
			$(this).find(".ui-article-thumb").removeAttr("href");
			$(this).find(".ui-article-title, .ui-article-description, .custom-read-more").wrapAll("

");
			$(this).attr("tabindex","0");

			if(!$(".ui-article-thumb", this).length){
				$(".item-detail-wrapper", this).css("display","block");
				$(this).addClass("no-image");
			} else {
				$(this).addClass("image");
			}

            $(".item-details", this).append("
");

		});	

		$("#hp-content-top .ui-article-title, #hp-content-top .ui-article-description, #hp-content-top .custom-read-more").show();

		$("#hp-content-top .headlines li.image").hover(function(e) {
			$(this).toggleClass("hovered");
			$(".item-detail-wrapper", this).slideDown().attr("aria-hidden","false");
		}, function() {
			$(this).toggleClass("hovered");
			$(".item-detail-wrapper", this).slideUp().attr("aria-hidden","true");

		});

		$("#hp-content-top .headlines li").on('focus', function () {
            if($(this).hasClass("hovered")){
            	//DO NOTHING
            } else {
            	$(".item-detail-wrapper", this).slideDown().attr("aria-hidden","false");
                $(this).addClass("hovered");
            }

		});

        $(".mobile-headline-close-btn").click(function(){
        	$(this).parent().parent().slideUp().attr("aria-hidden","true");
            $(this).parentsUntil(".image").removeClass("hovered");
        });

        $("#hp-content-top .headlines li").on('focusout', function () {
        	var $elem = $(this);
            setTimeout(function() {
                var hasFocus = !! ($elem.find(':focus').length > 0);
                if (! hasFocus) {
                    $($elem).removeClass("hovered");
					$(".item-detail-wrapper", $elem).slideUp().attr("aria-hidden","true");
                }
            }, 10);


        });

		$("#hp-content-top .headlines a.ui-article-thumb").click(function(e){
			e.preventDefault();
		});
	}

