/* ------------------------------   MEGA SCHOOLS  ------------------------------ */
/* ------------------------------   ____________________   ------------------------------ */   
	function megaSchools(){

		head.ready("schoolMinibase", function(){

			$.schoolListMinibase({
	            'miniBaseDomainID': 1,
	            'miniBaseModuleInstanceID': 17253,
	            'cache': true,
	            'schoolListReady': function(schoolList) {
	                /*
	                The returned value is an array of objects and each object is a school record.
	                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
	                */

	                //CREATE AN EMPTY ARRAY FOR SCHOOL CATEGORIES
                    schoolCategories = [];

                    //LOOP THROUGH EACH SCHOOL TO COLLECT CATEGORY NAMES
                    for(var i in schoolList) {
                    	var schoolType = schoolList[i].schoolType.split(" ");

                        //IF THE CATEGORY DOES NOT ALREADY EXIST IN THE ARRAY, PUSH IT INTO THE ARRAY
                        schoolCategories.indexOf(schoolType[1]) === -1 ? schoolCategories.push(schoolType[1]) : "";  
                    }

                    //BUILD THE SCHOOL LIST CONTAINERS BASED ON THE DETERMINED CATEGORIES
                    schoolCategoriesHTML = '';

           			//LOOP THROUGH EACH CATEGORY AND CREATE THE HTML FOR IT
                    for(var i in schoolCategories) {
                    	if(i == 0){
                        	//THIS IS THE FIRST CATEGORY, WE NEED TO ADD THE SCHOOL IMAGE HOLDER TO IT
                        	schoolCategoriesHTML += '
'+
                                                        ''+schoolCategories[i]+''+
                                                        ''+
                                                        ''+
                                                    '';
                        } else {

                            schoolCategoriesHTML += '
'+
                                                        ''+schoolCategories[i]+''+
                                                        ''+
                                                    '';
                        }
                    }

                    //INSERT THE SCHOOL LIST CONTAINERS INTO THE PAGE
                    $("#ac-school-list").append(schoolCategoriesHTML);

                    //CREATE AN EMPTY VARIABLE FOR SCHOOL IMAGE
                    var schoolImage = '';

					//LOOP THROUGH EACH SCHOOL, AND PLACE IT IN THE PROPER LIST
	                for(var i in schoolList) {
                    	var schoolCat = schoolList[i].schoolType.toLowerCase().split(" ");

	                    if(schoolList[i].schoolVisible == 'true' || schoolList[i].schoolVisible == 'TRUE') {

                                    schoolImage = schoolList[i].schoolImage;

	                            	if(!schoolImage){
	                                	schoolImage = 'https://lh5.googleusercontent.com/-BOS0wS3UFdg/AAAAAAAAAAI/AAAAAAAAAAA/gY4r_UxYdYA/s128-c-k/photo.jpg';
	                                }

	                            	$("#"+schoolCat[1]+"-schools").append('
' + schoolList[i].schoolName + '');

                                    //ADD THE SCHOOL IMAGE INTO THE SCHOOL IMAGE CONTAINER
	                                $("#school-image").append("");
	                    }

                    }

                    //CALL THE RESPONSIVE MENU FUNCTION AND PASS THE SCHOOL LIST TO IT
                    rsMenu(schoolList);
	            } 
	        });    
		});

	}

    function schoolListNav(){

    	//SET THE FIRST VISIBLE IMAGE
        //$("#school-image img:first").remove();
        var firstImage = $(".school-listing:first .school:first").attr("data");

        $("#school-image img").each(function(){
            if($(this).attr("src").indexOf(firstImage) == 0){
                $(this).addClass("hovered");
            }
        });

        //SCHOOLS IMAGE HOVER
        $(".school > a").hover(function(){
            var currentSchoolImage = $(this).parent().attr("data");

            $("#school-image img").removeClass("hovered");

            $("#school-image img").each(function(){
                if($(this).attr("src").indexOf(currentSchoolImage) == 0){
                    $(this).addClass("hovered");
                }
            });
        });

    	$(".school-type-heading").first().addClass("open");
        $(".school-type-heading").first().find(".school-listing").addClass("flex-me");

        $(".school-type-heading > a").on('click keypress', function(event){
            if(globalClick(event) === true){
                event.preventDefault();
                $(".school-type-heading").removeClass("open").attr("aria-selected", "false");
                $(this).parent().addClass("open").attr("aria-selected", "true");
                $(".school-listing").removeClass("flex-me").attr("aria-hidden", "true");
                $(this).parent().find(".school-listing").addClass("flex-me").attr("aria-hidden", "false");
            }
        });

        $(".school-type-heading > a").on('focus', function(event){
              event.preventDefault();
              $(".school-type-heading").removeClass("open").attr("aria-selected", "false");
              $(this).parent().addClass("open").attr("aria-selected", "true");
              $(".school-listing").removeClass("flex-me").attr("aria-hidden", "true");
              $(this).parent().find(".school-listing").addClass("flex-me").attr("aria-hidden", "false");
        });

        $("#close-mega-schools, .screen-cover").on('click keypress', function(event){
            if(globalClick(event) === true){
                $(".screen-cover").hide();
                $("#mega-schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                $("#mega-schools").removeClass("open");
                $("#mega-schools .selector").focus();
            }
        });


    }

    function schoolListSize(){
    	//ADJUST SCHOOLS CONTAINER SIZE FOR SHORT SCREENS
        wh = $(window).height();
        if(wh <= 678){
        	$(".ac-dropdown-container.schools .ac-dropdown").addClass("small-window");
        } else {
        	$(".ac-dropdown-container.schools .ac-dropdown").removeClass("small-window")
        }
    }

