	function dynamicContent() {

		// ********** MYSTART ********** //
        $(document).on("click", ".ac-dropdown-container .selector", function(e) {
                if($(this).parent().hasClass("open")){
                    if($(this).parent().hasClass("schools")){
                        $(".screen-cover").hide();
                    }
                    $(this).parent().find(".ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                    $(this).parent().removeClass("open"); 
                } else {
                    if($(this).parent().hasClass("schools")){
                        $(".screen-cover").show();

                        $(this).parent().find(".ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){
                            setTimeout(function(){
                                $(".ac-dropdown-container.schools li.school-type-heading:first-child > a").focus();
                            }, 100);
                        });
                    } else {
                    	$(this).parent().find(".ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){
                        	setTimeout(function(){
                            	$(this).parent().find(".ac-dropdown").focus();
                        	}, 100);
                    	});
                    }


                    $(this).parent().addClass("open");
                }	
		});

        $(".ac-dropdown-container").not(".schools").hover(function(e) {
			e.preventDefault();
		}, function() {

			$(".ac-dropdown",this).attr("aria-hidden", "true").slideUp(300, "swing"); 
			$(this).removeClass("open"); 
		});

		//SHOW USER OPTIONS IF THEY ARE LOGGED IN
		if($("#sw-mystart-account").length){
        	$(".ac-dropdown-container.user-options").show();
        } else {
        	 //HIDE REGISTER BUTTON IF HIDDEN IN SITE MANAGER
              if(!$("#ui-btn-register").length){
                  $(".ac-mystart-button.signin").show();
              } else {
              	$(".ac-mystart-button.user").show();
              }

        }


		// ********** HEADER ********** //

		// SEARCH
		$("#search-control").click(function(event){

				//DONT LET THE PAGE JUMP ON KEYPRESS
	            event.preventDefault();
	            if($(this).hasClass("open")){
                	if($(".mobile.ios").length){
                    	$("#header-right-bottom").removeClass("open-search");
                    	$("#sw-mystart-search").animate({
                            width: "0"
                        }, 1000, function() {
                            $("#sw-mystart-search").hide().css("visibility", "hidden");
                        });
                        $(this).removeClass("open");
                        $(this).find(".a11y").text("Click to Open Search");
                    } else {
                    	$("#sw-mystart-search").animate({
                            width: "0"
                        }, 1000, function() {
                            $("#sw-mystart-search").css("visibility","hidden");
                        });
                        $(this).removeClass("open");
                        $(this).find(".a11y").text("Click to Open Search");
                    }


	            } else {
                	if($(".mobile.ios").length){
                    	$("#header-right-bottom").addClass("open-search");
                    	$("#sw-mystart-search").show().css("visibility","visible").animate({
                            width: "240"
                        }, 1000, function() {
                            $("#sw-search-input").focus();
                        });
                        $(this).addClass("open");
                        $(this).find(".a11y").text("Click to Close Search");						
                    } else {
                    	$("#sw-mystart-search").css("visibility","visible").animate({
                            width: "240"
                        }, 1000, function() {
                            $("#sw-search-input").focus();
                        });
                        $(this).addClass("open");
                        $(this).find(".a11y").text("Click to Close Search");
                    }

	            }
    	});

        if($(".mobile.ios").length){
        	$("#gb-social-icons a:visible:last").css("margin-right","25px");
        }



		// ADD LOGO
		var logoCheck = "true";
		var logoSrc = jQuery.trim("/cms/lib/IL01000592/Centricity/Template/GlobalAssets/images///logos/decatur-logo.png");
		if(logoCheck == "true") {
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
				$("#gb-logo").append('').removeClass("hidden");
			} else {
				$("#gb-logo").append('').removeClass("hidden");
			}
		}

		// ADD SITENAME
		var siteNameOne = jQuery.trim('');
		var siteNameTwo = jQuery.trim('');
		var siteTagline = jQuery.trim('Educating for Success');

		if((siteNameOne == "") && (siteNameTwo == "")) {
			var splitLen = 3;
			var siteName = "Decatur Public Schools";
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			if(siteNameLength > 3){
				siteNameEnd = jQuery.trim(siteName.splice(-splitLen, siteName.length).toString().replace(/,/g, " "));
				siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
				$("#gb-sitename").prepend("
" + siteNameBegin + "
" + siteNameEnd + "
"+siteTagline+"");

            } else {
				siteNameEnd = jQuery.trim(siteName.splice(-1, siteName.length).toString().replace(/,/g, " "));
				siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
				$("#gb-sitename").prepend("
" + siteNameBegin + " " + siteNameEnd + "
"+siteTagline+"");
            }

		} else if((siteNameOne != "") && (siteNameTwo == "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
"+siteTagline+"");
		} else if((siteNameOne == "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameTwo + "");
		} else if((siteNameOne != "") && (siteNameTwo != "")) {
			$("#gb-sitename").prepend("
" + siteNameOne + "
" + siteNameTwo + "
"+siteTagline+"");
		}

        //CHANGE PADDING TO ACCOMODATE 3 LINES OF TEXT IN LARGE SCHOOL NAMES
        if($("#gb-sitename h1").height() > 65){
        	$("#gb-sitename").css("padding-top","11px");
        }

		// ********** CHANNEL BAR ********** //
		$(".sw-channel-item").unbind('hover');
		$(".sw-channel-item").hover(function(){
			$(".sw-channel-item ul").stop(true, true);
			var subList = $(this).children('ul');
			if ($.trim(subList.html()) !== "") {
				subList.slideDown(300, "swing");
			}
			$(this).addClass("hover");
		}, function(){
			$(".sw-channel-dropdown").slideUp(300, "swing");
			$(this).removeClass("hover");
		});

		// ********** BODY ********** //


		// AUTO FOCUS SIGN IN FIELD
		$("#swsignin-txt-username").focus();

		//PLACE THE HEADLINES READ MORE BUTTON THE THE APP FOOTER
		$(".headlines").each(function(){
        	if (("a.more-link", this).length) { //added if statement to surround existing code by Lyndsay - Case 02822348 - 7.6.17
				var moreLink = $(this).find("a.more-link").attr("href");
				$(".ui-widget-footer", this).prepend("VIEW ALL").addClass("show-more");
            }
		});

        //SET HP TOP HEADLINES CONTAINER WIDTH
        var numHeadlines = $("#hp-content-top .headlines li").length;
    	if(numHeadlines % 3 == 0){
        	$("#hp-content-top .ui-articles").css("max-width","1190px");
        }

		//PLACE THE UPCOMING EVENTS VIEW ALL BUTTON INTO THE APP FOOTER
		$(".upcomingevents").each(function(){
			var viewEventsURL = $(this).find("a.view-calendar-link").attr("href");
			$(this).find(".ui-widget-footer").append(""+
            	"EXPAND EVENTS"+
                ""+
                	""+
                ""+
            ""+
            ""+
            	"VIEW FULL CALENDAR"+
            ""+
            "
"+
            	"
"+
                "
"+
            "'");

        //CHECK FOR CONTAINER WIDTH
        var contWidth = $(this).width();

        if(contWidth <= 600){
        	$(this).find(".ui-articles").css("display","block")
        }

        });


		//FIX FIRST BREADCRUMB
        $(".ui-breadcrumb-first span").text("Home").show();

		//CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //ADD SIGNED OUT BREADCRUMB
            $(".ui-breadcrumbs").append("
Signed Out");
        }

        //ADD SIGN IN BREADCRUMB
        if($("#swlogin").length){
            $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
            $(".ui-breadcrumbs").append("
Sign In");
        }

        //REGISTER BREADCRUMB
		if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
			$("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
			$(".ui-breadcrumbs").append("
Register");
		}

		//SEARCH RESULTS BREADCRUMB
		if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
			$("#sw-content-layout-wrapper").children().wrapAll('

');
			$(".ui-breadcrumbs").append("
Search Results");
		}

        // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
        $("div.ui-widget.app .ui-widget-detail img")
            .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
            .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
            .each(function() {
                if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                    $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                }
        });

        // MAKE NAVIGATION APPS ACCESSIBLE
        head.ready("accessibleNavApp", function(){
            $(".ui-widget.app.navigation").csAccessibleNavigationApp();
        });


		// ********** FOOTER ********** //
		// MOVE SCHOOLWIRES FOOTER STUFF
		$(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(0) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(2) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-links li:eq(1) a"));
		$(".gb-schoolwires-footer.links").append($("#sw-footer-copyright"));	


	}  
