	function modEvents(){
		head.ready("twoColumnEvents", function(){
			if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "no",
		            monthLong   : "yes",
		            dayWeek     : "yes"
		        });
		        eventsByDay(".upcomingevents .ui-articles");
		    }
		});
	}

	function eventsByDay(container) {
	    var countLi = 0;
	    $(".ui-article", container).each(function(){
	        if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
	            var moveArticle = $(this).html();
	            $(this).parent().prev().children().children().next().append(moveArticle);
	            $(this).parent().remove();
	        };

            if(!$(".sw-calendar-block-time", this).length){
            	$("ALL DAY").prependTo($(this).find(".ui-article-description"));
			}

            $('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});

			//WRAP DAY NAME AND MONTH IN A CONTAINER
			$(".joel-day, .joel-month", this).wrapAll("");
			var dayMonth = $(".adam-hug", this).html();
			var dayNumber = $("h1", this).text();
			var number = parseInt(dayNumber.replace(/[^0-9\.]/g, ''), 10);
			$("h1",this).html(""+number+""+dayMonth+"");
	    }); 





	}	

	function eventA11y(){
		$(".upcomingevents li").each(function(){
			var elementPosition = $(this).position().top;
			if(elementPosition > 75){

				$(this).find("a").attr("tabindex","-1");
			}
		});
	}

	//EXPAND EVENT FUNCTIONALITY IN TOP UPCOMING EVENTS
	function expandEvents(){
		$(".expand-events").on('click keypress', function(event){
            if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
                event.preventDefault();

                var events = $(this).parent().parent().find(".ui-articles");
                var clickedButton = $(this);
                var initialHeight = 201;
                var totalHeight = events.get(0).scrollHeight;

                if(events.hasClass("open")){
                	events.animate({
						height: initialHeight
					}, 1000, function() {
						eventA11y();
						events.removeClass("open");
						clickedButton.removeClass("open").html('EXPAND EVENTS');
					});
                } else {
                	events.animate({
						height: totalHeight
					}, 1000, function() {
						events.find("a").attr("tabindex","0");
						events.addClass("open");
						clickedButton.addClass("open").html('COLLAPSE EVENTS');
					});
                } 
            }
         });
	}

