	function extraRotatorElements(){
		$(".rs-gallery-more-link").after("");

		$("#view-page-content").on('click keypress', function(event){
            if(globalClick(event) === true){
				//DONT LET THE PAGE JUMP ON KEYPRESS
                event.preventDefault();
		    		$('html, body').animate({
				        scrollTop: $(this).offset().top
				}, 500, function(){
				    	$(".global-icon:first").focus();
				});
		    }
		});
	}


