	function rsMenu(schoolList) {
    	var mobileSchools = '1, District Home, /, _blank;';

        for(var i in schoolList) {
            if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
            }
        }

    	head.ready("rsMainMenu", function(){
            // RESPONSIVE MAIN MENU
            $("body").csRsMenu({
                "breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
                "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
                "primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
                "secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
                "accentColor"           : "#BABABA", // DEFAULT - #BABABA
                "menuButtonParent"      : "#gb-channel-list-outer",
                "showMenuBtnText"       : "yes",
                "menuBtnText"           : "MENU",
                "showSchools"           : "no",
                "showTranslate"         : "yes",
                "translateId"           : "none",
                "showAccount"           : "yes",
                "extraMenuOptions"      : "Schools", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
                "extraMenuItems"        : mobileSchools, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET; 
                "siteID"                : "1",
                "allLoaded"             : function(){ }
            });
		});
    }

    function rsPageMenu(){
    	head.ready("mobilePageList", function(){
    		$("body").csRsPagelistMenu({
				"breakPoint"            : 768, // SYSTEM BREAK POINTS - 768, 640, 480, 320
				"slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
				"primaryColor"          : "#1A1A1A", // DEFAULT - #1A1A1A
				"secondaryColor"        : "#0C0C0C", // DEFAULT - #0C0C0C
				"accentColor"           : "#BABABA", // DEFAULT - #BABABA
				"menuButtonParent"      : "#gb-channel-list-outer",
				"showMenuBtnText"       : "yes",
				"menuBtnText"           : "PAGES",
				"siteID"                : "1",
				"allLoaded"             : function(){ }
			});
    	});
    }
