	function accessibilityFunctions(){
        $("#sw-search-button").attr("aria-label", "Click to search");

		$("#sw-mystart-mypasskey").attr({
			"aria-label":	"Open My Passkey",
			"role":		"button"
		});

		// KEY MAPPING
		var csKeyCodes = { "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40, "tab": 9 };


		$(".ac-dropdown-container.translate .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.translate").hasClass("open")){
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.translate").removeClass("open"); 
					} else {
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown-container.translate li:first-child > a").focus();
							}, 100);
						});
						$(".ac-dropdown-container.translate").addClass("open");
					}

				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.translate").removeClass("open");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container.translate .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.translate").removeClass("open");
						$(".ac-mystart-button.home a").focus();
					}

				break;

				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.translate .ac-dropdown-list li:first-child > a").focus();

				break;
			}
		});

		$(".ac-dropdown-container.translate li a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container.translate .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container.translate .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM

						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container.translate .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container.translate .ac-dropdown-list li:last-child > a").focus();
				break;

			}
		});

		$(".ac-dropdown-container.user-options .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.user-options").hasClass("open")){
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.user-options").removeClass("open"); 
					} else {
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown-container.user-options li:first-child > a").focus();
							}, 100);
						});
						$(".ac-dropdown-container.user-options").addClass("open");
					}	

				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.user-options").removeClass("open");
					} else {
						e.preventDefault();
						$(".ac-dropdown-container.user-options .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.user-options").removeClass("open");
						$(".ac-mystart-button.sitemap a").focus();
					}

				break;

				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.user-options .ac-dropdown-list li:first-child > a").focus();

				break;
			}
		});

		$(".ac-dropdown-container.user-options li a").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(".ac-dropdown-container.user-options .selector").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(".ac-dropdown-container.user-options .ac-dropdown-list li:first-child > a").focus();
					} else {
						// FOCUS NEXT ITEM

						$(this).parent().next("li").find("> a").focus();
					}
				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".ac-dropdown-container.user-options .ac-dropdown-list li:first-child > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".ac-dropdown-container.user-options .ac-dropdown-list li:last-child > a").focus();
				break;

			}
		});


        $(".ac-dropdown-container.schools .selector").keydown(function(e) {
			// CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.enter:
				case csKeyCodes.space:
					e.preventDefault();

					//IF THE DROPDOWN IS OPEN, CLOSE IT
					if($(".ac-dropdown-container.schools").hasClass("open")){
						$(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.schools").removeClass("open");
                        $(".screen-cover").hide();
					} else {
                    	 $(".screen-cover").show();
						$(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden","false").slideDown(300, "swing", function(){

							setTimeout(function(){
								$(".ac-dropdown-container.schools li.school-type-heading:first-child > a").focus();
							}, 100);
						});
						$(".ac-dropdown-container.schools").addClass("open");
					}

				break;

				// CONSUME TAB KEY
				case csKeyCodes.tab:
					if(e.shiftKey){
						$(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.schools").removeClass("open");
                        $(".screen-cover").hide();
					} else {
                    	$(".screen-cover").hide();
						$(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
						$(".ac-dropdown-container.schools").removeClass("open");

                        setTimeout(function(){
								$("#gb-social-icons a:first-child").focus();
							}, 100);
					}

				break;

				// CONSUME DOWN AND RIGHT ARROWS
				case csKeyCodes.down:
				case csKeyCodes.right:
					e.preventDefault();

					$(".ac-dropdown-container.schools li:first a").focus();

				break;
			}
		});


		//SCHOOL TYPES (TAB HEADINGS)
		$(".ac-dropdown-container.schools li.school-type-heading > a").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {

				//CONSUME RIGHT KEY 
				case csKeyCodes.right:
                case csKeyCodes.down:
                	e.preventDefault();
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$("#close-mega-schools").focus();
					} else {
						// FOCUS NEXT ITEM
						$(this).parent().next().find("> a").focus();
					}
				break;

				//CONSUME LEFT KEY 
				case csKeyCodes.left:
                case csKeyCodes.up:
                	e.preventDefault();
					if($(this).parent().is(":first-child")) {
						// FOCUS LAST ITEM
						$("#close-mega-schools").focus();
					} else {
						// FOCUS PREV ITEM
						$(this).parent().prev().find("> a").focus();
					}
				break;

                //CONSUME TAB KEY
				case csKeyCodes.tab:


					if(e.shiftKey){
                    	e.preventDefault();
						$(".screen-cover").hide();
                        $(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
                        $(".ac-dropdown-container.schools").removeClass("open");
                        $(".ac-dropdown-container.schools .selector").focus();
					} else {
                        //$(this).parent().find(".school-listing li:first > a").focus();
					}

				break;

				// CONSUME HOME KEY
				case csKeyCodes.home:
					e.preventDefault();

					// FOCUS FIRST ITEM
					$(".schools .school-type-heading:first > a").focus();
				break;

				// CONSUME END KEY
				case csKeyCodes.end:
					e.preventDefault();

					// FOCUS LAST ITEM
					$(".schools .school-type-heading:last > a").focus();
				break;

                //ESC KEY
                case csKeyCodes.esc:

                	$(".screen-cover").hide();
					$(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
					$(".ac-dropdown-container.schools").removeClass("open");
                    $(".ac-dropdown-container.schools .selector").focus();

                break;

			}
		});

        //SCHOOLS WITHIN A TAB
		$(".ac-dropdown-container.schools .school-listing li a").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {
				// CONSUME LEFT AND UP ARROWS
				case csKeyCodes.left:
				case csKeyCodes.up:
					e.preventDefault();

					// IS FIRST ITEM
					if($(this).parent().is(":first-child")) {
						// FOCUS DROPDOWN BUTTON
						$(this).parent().parent().find("li:last > a").focus();
					} else {
						// FOCUS PREVIOUS ITEM
						$(this).parent().prev("li").find("> a").focus();
					}
				break;

				// CONSUME RIGHT AND DOWN ARROWS
				case csKeyCodes.right:
				case csKeyCodes.down:
					e.preventDefault();

					// IS LAST ITEM
					if($(this).parent().is(":last-child")) {
						// FOCUS FIRST ITEM
						$(this).parent().siblings(":first").find("> a").focus();
                    } else {
						// FOCUS NEXT ITEM
						$(this).parent().next().find("> a").focus();
					}
				break;

                case csKeyCodes.esc:

                	$(".screen-cover").hide();
					$(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
					$(".ac-dropdown-container.schools").removeClass("open");
                    $(".ac-dropdown-container.schools .selector").focus();

                break;

			}
		});

		$("#close-mega-schools").keydown(function(e) {

            // CAPTURE KEY CODE
			switch(e.keyCode) {

				//CONSUME TAB KEY
				case csKeyCodes.tab:
					e.preventDefault();

					if(e.shiftKey){
						$(".schools .school-type-heading:last > a").focus();
					} else {
						$(".schools .school-type-heading:first > a").focus();
					}

				break;

                //LEFT AND UP KEYS
                case csKeyCodes.left:
                case csKeyCodes.up:
                	$(".schools .school-type-heading:last > a").focus();
                break;

                //RIGHT AND DOWN KEYS
                case csKeyCodes.right:
                case csKeyCodes.down:
                	$(".schools .school-type-heading:first > a").focus();
                break;

                case csKeyCodes.esc:

                	$(".screen-cover").hide();
					$(".ac-dropdown-container.schools .ac-dropdown").attr("aria-hidden", "true").slideUp(300, "swing"); 
					$(".ac-dropdown-container.schools").removeClass("open");
                    $(".ac-dropdown-container.schools .selector").focus();

                break;
			}
		});
	}
