	function jsMediaQueries() {
		switch(csGetBreakPoint()) {
			case "desktop": 
				$("#gb-global-icons").insertAfter("#sticky-header");
                $(".hp #gb-sitename").prependTo("#gb-header");
			break;
			case "768":
				$("#gb-global-icons").insertAfter("#gb-schoolwires-footer-outer");
                $(".hp #gb-sitename").prependTo("#gb-header");
			break;
			case "640":
				$("#gb-global-icons").insertAfter("#gb-schoolwires-footer-outer");
                $(".hp #gb-sitename").prependTo("#gb-channel-list");
			break;
			case "480":
				$("#gb-global-icons").insertAfter("#gb-schoolwires-footer-outer");
                $(".hp #gb-sitename").prependTo("#gb-channel-list");
			break;
			case "320":
				$("#gb-global-icons").insertAfter("#gb-schoolwires-footer-outer");
                $(".hp #gb-sitename").prependTo("#gb-channel-list");
			break;
		}
	}

    function stickyHeader() {
    	if (!isMyViewPage) {
        	switch(csGetBreakPoint()) {
                case "desktop": 
                	if ($(window).scrollTop() >= 1 && !headerIsSticky) {
                    	$('#sticky-header').addClass('sticky');
                    	$('body').css('margin-top', $('#sticky-header').outerHeight(true));
                        headerIsSticky = true;
                   } else if ($(window).scrollTop() < 1 && headerIsSticky) {
                        $('#sticky-header').removeClass('sticky');
                        $('body').removeAttr('style'); 
                        headerIsSticky = false;
                   }    
                break;
                case "768":
                    $('body').removeAttr('style'); 
                    headerIsSticky = false;
                    break;
                case "640":
                	$('body').removeAttr('style'); 
                    headerIsSticky = false;
                    break;
                case "480":
                	$('body').removeAttr('style'); 
                    headerIsSticky = false;
                    break;
                case "320":
                	$('body').removeAttr('style'); 
                    headerIsSticky = false;
                    break;
            }    
        }
    }

    function sameHeights() {
    	$("body").csRsSameHeights({
              resizeElements : [
                      {
                              "resizeMethod"          : "columns",            // OPTIONS - columns, apps
                              "parent"                : "#hp-content-top",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
                              "columns"               : ".hp.column",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
                              "gutter"                : 10,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
                              "applySameHeights"      : ["960", "768"]        // OPTIONS - ["960", "768", "640", "480", "320"]
                      }
              ]
      });
    }

    function subpageSameHeights() {
    	$("body").csRsSameHeights({
              resizeElements : [
                      {
                              "resizeMethod"          : "columns",            // OPTIONS - columns, apps
                              "parent"                : "#sp-content",        // PARENT THAT COLUMNS AND/OR APPS ARE IN THAT ARE GETTING RESIZED
                              "columns"               : ".sp.column",         // COLUMNS AND/OR APPS WITHIN THESE COLUMNS THAT SAME HEIGHTS IS BEING APPLIED TO -- NEEDS TO BE A CLASS
                              "gutter"                : 10,                   // PIXEL SPACING BETWEEN APPS -- WILL BE ADDED AS BOTTOM MARGIN
                              "applySameHeights"      : ["960", "768"]        // OPTIONS - ["960", "768", "640", "480", "320"]
                      }
              ]
      });
    }

