    function checkSlideshow() {
		if($(".hp").length && csGetBreakPoint() != "desktop") {
			if ($(window).scrollTop() <= $("#hp-slideshow").offset().top + $("#hp-slideshow").height()) {
				if(csSlideshowDescFixed) {
					$("#hp-slideshow .rs-gallery-desc-outer").removeAttr("style");

					csSlideshowDescFixed = false;
				}
			} else {
				if(!csSlideshowDescFixed) {
					$("#hp-slideshow .rs-gallery-desc-outer").css({
						"height": $("#hp-slideshow .rs-gallery-desc-outer").height(),
						"overflow": "hidden"
					});

					csSlideshowDescFixed = true;
				}
			}
		}
	}

