	function searchText() {
        //SEARCH TEXT FOR DESKTOP
        if ($(window).width() > 767) {
            $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
            $("div#sw-mystart-search input#sw-search-input").attr("value", "search our site...");

            $("div#sw-mystart-search input#sw-search-input").focus(function() {
                if($(this).val() == "search our site...") {
                    $(this).val("");
                }
            });

            $("div#sw-mystart-search input#sw-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("search our site...");
                }
            });
        }
        //SEARCH TEXT FOR 640 SCREEN SIZE AND SMALLER
        else {
            $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
            $("div#sw-mystart-search input#sw-search-input").attr("value", "search");

            $("div#sw-mystart-search input#sw-search-input").focus(function() {
                if($(this).val() == "search") {
                    $(this).val("");
                }
            });

            $("div#sw-mystart-search input#sw-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("search");
                }
            });
        }
	}

    function modEvents(){
    	head.ready("modEvents", function(){
            if (!$(".upcomingevents .joel-month").length) {
                $(".upcomingevents").modEvents({
                    columns     : "yes",
                    monthLong   : "no",
                    dayWeek     : "no"
                });
                eventsByDay(".upcomingevents .ui-articles");
                $(".upcomingevents .ui-article-description").each(function() {
                    if(!$(".sw-calendar-block-time", this).length) {
                        $(this).prepend('All Day');
                    }
                    $(".joel-month").append(" ");
                });
            }
    	});
    }

    function eventsByDay(container) {
        var countLi = 0;
        $(".ui-article", container).each(function(){
            if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                var moveArticle = $(this).html();
                $(this).parent().prev().children().children().next().append(moveArticle);
                $(this).parent().remove();
            };
        });
    }

	function calendarListView() {
		// AUTO CLICK FUNCTION FOR SCHOOLWIRES CALENDAR LIST VIEW FOR SMALL MOBILE DEVICES
		if(!$(".ui-widget.app.calendar #calendarlist-pnl-specialview.loaded").length){
			$("a.ui-btn-toggle.list").click();
			$("a.ui-btn-toggle.month").click();
			$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("loaded");
		}

		switch(csGetBreakPoint()) {
			case "desktop": case "768": case "640":
				$(".ui-widget.app.calendar #calendar").removeClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").addClass("ui-helper-hidden");
			break;
			case "480": case "320":
				$(".ui-widget.app.calendar #calendar").addClass("ui-helper-hidden");
				$(".ui-widget.app.calendar #calendarlist-pnl-specialview").removeClass("ui-helper-hidden");
				$(".ui-btn-toggle.month").removeClass("active");
				$(".ui-btn-toggle.list").addClass("active");
			break;
		}
	}

	function csGetBreakPoint() {
		return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
	}
      function addFramesetTranslate() {
    	//TRANSLATE
        $("#gb-mystart-left").creativeTranslate({
            "type": 1,
            "advancedOptions": {
                "addMethod": "append", // PREPEND OR APPEND THE TRANSLATE ELEMENT
                "dropdownHandleText": 'languages', // ONLY FOR FRAMESET AND API VERSIONS
                "useTranslatedNames": true, // ONLY FOR FRAMESET AND API VERSIONS - true = TRANSLATED LANGUAGE NAME, false = ENGLISH LANGUAGE NAME
            },
            "translateLoaded": function() { // CALLBACK TO RUN YOUR CUSTOM CODE AFTER EVERYTHING IS READY
            	var pageURL = window.location.href;
				pageURL = encodeURIComponent(pageURL);
                	$(".spanish-translate").attr("href", '//translate.google.com/translate?u=' + pageURL + '&langpair=en|es&hl=en&anno=0&ie=UTF8');
                }
        });
    }

    function modMyStart() {
    	addFramesetTranslate();
    }

	$(function() {
        if(false == true && !$("html").hasClass("ios")) {
            //loads the IFrame Player API code asynchronously.
            var tag = document.createElement('script');
            tag.src = "https://www.youtube.com/iframe_api";
            var firstScriptTag = document.getElementsByTagName('script')[0];
            firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);
        }
    });

    //creates an iframe (and YouTube player) after the API code downloads.
    var player;
    var windowWidth;
    var windowHeight;
    function onYouTubeIframeAPIReady() {
        if(false == true) {
            windowWidth = $(window).width();
            windowHeight = $(window).height();
            player = new YT.Player('hp-slideshow-video', {
                height: windowHeight,
                width: windowWidth,
                videoId: '8JQGXlaQMV4',
                playerVars: { 
                    'modestbranding': 1,
                    'controls': 0, 
                    'rel' : 0,
                    'loop': 1,
                    'wmode': 'opaque'
                },
                events: {
                    'onReady': onPlayerReady,
                    'onStateChange': onPlayerStateChange
                }
            });
        }
    }

    //the API will call this function when the video player is ready.
    function onPlayerReady(event) {
        if(!$("html").hasClass("mobile")) {
            event.target.playVideo();
        } else {
            $("#play-pause-btn").attr("aria-label", "Play Video").removeClass("pause");
        }

        csVideoResize();

        $(document).on("click keydown", ".pause-btn", function(e) {
            if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();

                $(this).addClass("pause");
                $(".play-btn").removeClass("pause");
                pausePlayVideo();
            }
        });

        $(document).on("click keydown", ".play-btn", function(e) {
            if(e.type == "click" || e.type == "keydown" && e.keyCode == csKeyCodes.space || e.type == "keydown" && e.keyCode == csKeyCodes.enter) {
                e.preventDefault();

                $(this).addClass("pause");
                $(".pause-btn").removeClass("pause");
                pausePlayVideo();
            }
        });
    }

    //The API calls this function when the players state changes.
    //The function indicates that when playing a video (state=1),
    //the player should play for six seconds and then stop.
    var done = false;
    function onPlayerStateChange(event) {
        if(!$("html").hasClass("mobile")) {
            pausePlayVideo();
        }
    }

    function pausePlayVideo() {
        if($(".play-btn").hasClass("pause")) {
            player.playVideo();
            player.mute();
        } else {
            player.pauseVideo();
        }
    }

    function stopVideo() {
        player.pauseVideo();
    }

    function csVideoResize() {
        if($("#hp-slideshow").length) {
            switch(csGetBreakPoint()) {
                case "desktop":
                    var topOffSet = $("#hp-slideshow").offset().top;
                    var screenWidth = $(window).width();
                    var screenHeight = $(window).height() - topOffSet;
                    var screenRatio = screenHeight / screenWidth;
                    var containerHeight = screenWidth * screenRatio;
                    var vidWidth = screenWidth, vidHeight = 0;

                    // GET IMAGE RATIO
                    var initW = 853.5, initH = 480;
                    var ratio = initH / initW;
                    vidHeight = vidWidth * ratio;

                    if(vidHeight < screenHeight){
                        vidHeight = screenHeight;
                        vidWidth = vidHeight / ratio;
                    }

                    var centerWidth = (vidWidth - screenWidth) / 2;
                    var centerHeight = (vidHeight - screenHeight) / 2;

                    // APPLY THE DIMENSIONS
                    $("#hp-slideshow").css({"height": containerHeight + "px"});
                    $("#hp-slideshow-video-outer #hp-slideshow-video-inner").css("height", containerHeight + "px");
                    $("#hp-slideshow-video-outer .rs-gallery-desc-bg-outer").css("height", containerHeight + "px");
                    $("#hp-slideshow-video").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
                break;
                case "768": case "640": case "480": case "320":
                    var screenWidth = $(window).width();
                    var screenHeight = $(window).height();
                    var vidWidth = screenWidth, vidHeight = 0;

                    // GET IMAGE RATIO
                    var initW = 1450, initH = 710;
                    var ratio = initH / initW;
                    vidHeight = vidWidth * ratio;

                    // APPLY THE DIMENSIONS
                    $("#hp-slideshow").css({"height": "auto"});
                    $("#hp-slideshow-video-outer #hp-slideshow-video-inner").css("height", vidHeight + "px");
                    $("#hp-slideshow-video-outer .rs-gallery-desc-bg-outer").css("height", vidHeight + "px");
                    $("#hp-slideshow-video").css({"width": "100%", "height": vidHeight + "px", "margin": "0px"});
                break;
            }
        }
    }

