        "ChannelBar": function() {
            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                	subList.stop(true, true).slideDown(300, "swing");

                    subList.css('right', '');

                    //(channel elements left position + width of the dropdown + an additonal offset of 15) - the width of the window
                   	let dropdownLeftOffset = subList.offset().left - 15;

                    if(dropdownLeftOffset < 0){
                    	subList.css('right', dropdownLeftOffset);
                    }
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });


            $("#channel-navigation").append($('#gb-search-container').addClass('show'));
