                var itemsAddedToList = 0;
                var maxItemsToAdd = 0;
                for( var d = 0; d < channelCats.length; d++ ){
                	maxItemsToAdd += (channelCats[d].channelCatSections != "") ? parseInt(channelCats[d].channelCatSections) : 0;
                }

                categoryColumn += '
';
                if(channelCats[0].channelCatSectionHeader != ""){
                    categoryColumn += '
' + channelCats[0].channelCatSectionHeader + '';
                }
                categoryColumn +=   '';

                $("> .sw-channel-dropdown li", this).each(function(liIndex, li){
                    if(channelCats[catCol].channelCatSections != ""){
                        categoryColumn += '
' + $(li).text() + '';
                        } else {
                            categoryColumn += '>' + $(li).text() + '';
                        }

                        itemsAddedToList++;
                        if(itemsAddedToList == channelCats[catCol].channelCatSections){
                            categoryColumn += 	'';
                            categoryColumn += '';
                            catCol++;
                            if(catCol == channelCats.length || liIndex == maxItemsToAdd - 1){
                                return false
                            }

                            categoryColumn += '
';
                            if(channelCats[catCol].channelCatSectionHeader != ""){
                                categoryColumn += '
' + channelCats[catCol].channelCatSectionHeader + '';
                            }
                            categoryColumn +=   '';
                            itemsAddedToList = 0;
                        }
                    }else{
                        return false
                    }
                });
                $(this).addClass('cols-' + catCol);

                categoryColumn +=	'';

                // ADD STRUCTURE TO THE DOM
                $(".sw-channel-dropdown", this).html('




');

                // ADD IMAGE
                var imgColumn = '';
                var imgSrc = jQuery.trim(megaMenuData[i].channelImg);
                var srcSplit = imgSrc.split("/");
                var srcSplitLen = srcSplit.length;
                var noImg = false;
                if(megaMenuData[i].channelImgBool != "false" && imgSrc != "" && srcSplit[srcSplitLen - 1] != "default-man.jpg") {
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top").addClass('has-thumb');
                    imgColumn = '
';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-one", this).append(imgColumn);
                }else{
                    noImg = true;
                    $(this).addClass('no-img');
                }

                // ADD CHANNEL NAME
                var noTitle = false;
                if(megaMenuData[i].channelTitle != ""){
                    var channelColumn = '
' + megaMenuData[i].channelTitle + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(channelColumn);
                }else{
                    noTitle = true;
                }

                // ADD CAPTION
                var noCaption = false;
                if(jQuery.trim(megaMenuData[i].channelCaption) != "" ){
                    var captionColumn = '
' + jQuery.trim(megaMenuData[i].channelCaption) + '';
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).append(captionColumn);
                }else{
                    noCaption = true;
                }

                if(noTitle && noCaption){
                    $(this).addClass('no-text');
                    $(".sw-channel-dropdown .mega-columns .mega-menu-top .mega-menu-top-col-two", this).remove();

                    if(noImg){
                        $('.sw-channel-dropdown .mega-menu-top', this).remove();
                        $('.sw-channel-dropdown .mega-columns', this).addClass('full-width');
                    }else{
                    	$('.sw-channel-dropdown .mega-columns', this).addClass('flex-cont');
                    }
                }

                $(".sw-channel-dropdown .mega-columns .mega-menu-bottom", this).append(categoryColumn);

                $('.sw-channel-dropdown .mega-channel-cat-outer li').addClass('mega-channel-cat-section-list-item');
            }); 

            $("#gb-channel-list .mega-channel .mega-channel-cat .mega-channel-cat-section-list-item a").keydown(function(e) {

                    var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

                    // CAPTURE KEY CODE
                    switch(e.keyCode) {
                        // CONSUME LEFT AND UP ARROWS
                        case csKeyCodes.left:
                        case csKeyCodes.up:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS FIRST ITEM
                            if($(this).parent().is(":first-child") && !$(this).parent().parent().parent().is(":first-child")) {
                                // FOCUS LAST ITEM
                                $(this).parent().parent().parent().prev(".mega-channel-cat").find(".mega-channel-cat-section-list li:last-child > a").focus();
                            } else if($(this).parent().is(":first-child") && $(this).parent().parent().parent().is(":first-child")) {
                                $('.mega-channel.hover > a').focus();
                            } else {
                                // FOCUS PREVIOUS ITEM
                                $(this).parent().prev("li").find("> a").focus();
                            }
                        break;

                        // CONSUME RIGHT AND DOWN ARROWS
                        case csKeyCodes.right:
                        case csKeyCodes.down:
                            e.preventDefault();
                            e.stopImmediatePropagation();

                            // IS LAST ITEM
                            if($(this).parent().is(":last-child") && !$(this).parent().parent().parent().is(":last-child")) {
                                // FOCUS LAST ITEM
                                $(this).parent().parent().parent().next(".mega-channel-cat").find(".mega-channel-cat-section-list li:first-child > a").focus();
                            } else if($(this).parent().is(":last-child") && $(this).parent().parent().parent().is(":last-child")) {
                            	$(this).parent().parent().parent().parent().find(".mega-channel-cat:first-child").find(".mega-channel-cat-section-list li:first-child > a").focus()    
                            } else {
                                // FOCUS NEXT ITEM
                                $(this).parent().next("li").find("> a").focus();
                            }
                        break;

                        // CONSUME HOME KEY
                        case csKeyCodes.home:
                            e.preventDefault();

                            // FOCUS FIRST ITEM
                            $(this).closest(".mega-channel-cat-section-list").find("li:first-child > a").focus();
                        break;

                        // CONSUME END KEY
                        case csKeyCodes.end:
                            e.preventDefault();

                            // FOCUS LAST ITEM
                            $(this).closest(".mega-channel-cat-section-list").find("li:last-child > a").focus();
                        break;
                    }
                });
        },
        "SiteShortcutDropdowns": function(){
        	// HOMEPAGE AUDIENCE SITE SHORTCUTS
            $("#shortcuts-dropdowns div.ui-widget.app.navigation").each(function(appIndex, app) {
                $(this).parent().parent().addClass("cs-nav-accordion");
                $(this).children().not($(".ui-widget-header", this)).wrapAll('
');
                 $("div.ui-widget-header", app)
                    .attr('aria-expanded', 'false')
                    .attr('aria-haspopup', 'true')
                    .attr('aria-controls', 'navigation-app-content-' + appIndex)
                    .attr("tabindex", 0);
            });
            $("#shortcuts-dropdowns .cs-nav-accordion").wrapAll('
');
            $("#shortcuts-dropdowns div.ui-widget.app.navigation div.ui-widget-header").attr("role", "button");
            $("#shortcuts-dropdowns div.ui-widget.app.navigation div.ui-widget-header h1").wrapInner('');

            //Click event
			$("#shortcuts-dropdowns div.ui-widget.app.navigation div.ui-widget-header").click(function(e){
            	CreativeTemplate.SiteShortcutActions($(this).parent());
            });
            //Keyboard enter and space event
            $("#shortcuts-dropdowns div.ui-widget.app.navigation div.ui-widget-header").keydown(function(e){
            	var dropdown = $(this).parent();

            	if(e.keyCode == CreativeTemplate.KeyCodes.enter || e.keyCode == CreativeTemplate.KeyCodes.space){
                	e.preventDefault();
                    CreativeTemplate.SiteShortcutActions(dropdown);

                    if( $(this).attr('aria-expanded') == 'true' ){
                    	$('a', dropdown).first().focus();
                    }

                    $('a', dropdown).attr('tabindex', -1).off('keydown').keydown(function(e){
                        var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                        var linkObj = $('a', dropdown);

                        switch(e.keyCode) {
                            // CONSUME LEFT AND UP ARROWS
                            case csKeyCodes.left:
                            case csKeyCodes.up:
                                e.preventDefault();
                                e.stopImmediatePropagation();

                                    // IS FIRST ITEM
                                if(linkObj.index($(this)) == 0) {
                                    $('.ui-widget-header', dropdown).focus();
                                } else {
                                    linkObj[linkObj.index($(this)) - 1].focus();
                                }
                            break;

                            // CONSUME RIGHT AND DOWN ARROWS
                            case csKeyCodes.right:
                            case csKeyCodes.down:
                                e.preventDefault();
                                e.stopImmediatePropagation();

                                // IS LAST ITEM
                                if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                                    // FOCUS FIRST ITEM
                                    linkObj[0].focus();
                                } else {
                                    // FOCUS NEXT ITEM
                                    linkObj[linkObj.index($(this)) + 1].focus();
                                }
                            break;

                            case csKeyCodes.tab:
                            	CreativeTemplate.SiteShortcutActions(dropdown);
                            break;

                            default: //pass
                            break;
                        }
                    });
                }else if(e.keyCode == 39 || e.keyCode == 40 && $(this).attr('aria-expanded') == 'true'){
                    e.preventDefault();
                    $('a', dropdown).first().focus();
                }else if( e.keyCode == 9 && $(this).attr('aria-expanded') == 'true' ){
                    if($(this).attr('aria-controls') != 'translate-option-list'){
                        CreativeTemplate.SiteShortcutActions(dropdown);
                    }
                }
            });


            // HOMEPAGE COLUMN ONE CHECK FOR UL STRUCTURE
			$("#shortcuts-dropdowns div.ui-widget.app").each(function() {
                if($(".ui-widget-detail > ul", this).length) {
                    $(this).addClass("ul-articles");
                }
			});
        },
        "SiteShortcutActions": function(app){
        	if($(app).hasClass("open")) {
                $(app).removeClass("open");
                $('.ui-widget-header', app).attr('aria-expanded', 'false');
                $("div.ui-widget-content", app).attr('aria-hidden', 'true');
            } else {
                $(app).addClass("open");
                $('.ui-widget-header', app).attr('aria-expanded', 'true');
                $("div.ui-widget-content", app).attr('aria-hidden', 'false');
            }

            $("div.ui-widget-content", app).slideToggle(200, function() {
                if($(app).hasClass("open")) {
                    //$(".ui-widget-content a", app).first().focus();
                }
            });
        },


