	//loads the IFrame Player API code asynchronously.
	var tag = document.createElement('script');
	tag.src = "//www.youtube.com/iframe_api";
	var firstScriptTag = document.getElementsByTagName('script')[0];
	firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

	//creates an iframe (and YouTube player) after the API code downloads.
	var player;
	var windowWidth;
	var windowHeight;
	function onYouTubeIframeAPIReady() {
        windowWidth = $(window).width();
        windowHeight = $(window).height();
        if( 'false' == "true" && typeof(YT) != 'undefined' || typeof(YT.Player) != 'undefined' && $('#youtube-video').length > 0){
            player = new YT.Player('youtube-video', {
                height: windowHeight,
                width: windowWidth,
                videoId: "9n-tWNvsVG0",
                playerVars: { 
                    'modestbranding': 1,
                    'controls': 0, 
                    'autoplay': 1,
                    'rel' : 0,
                    'loop': 1,
                    'suggestedQuality': 'highres',
                    'wmode': 'opaque'
                },
                events: {
                    'onReady': onPlayerReady,
                    'onStateChange': onPlayerStateChange
                }
            });
        }
	}

	//the API will call this function when the video player is ready.
	function onPlayerReady(event) {
    	CreativeTemplate.SlideshowObjectConstructor(); //this is here for mobile views added by Jeremy K - 03103575 
    	$("#youtube-video").attr('tabindex', -1);
		csVideoResize();
        player.mute()
		$(window).resize(csVideoResize);

        /*var playButton = document.getElementById("hp-video-play-pause");
        playButton.addEventListener("click", function() {
            if( $(this).hasClass('playing') ){
              player.pauseVideo();
              $(this).removeClass('playing')
              	.attr('aria-label', 'Play background video');
              $('i', this).removeClass('dev-icon-pause').addClass('dev-icon-play');
            }else{
              player.playVideo();
              $(this).addClass('playing')
              	.attr('aria-label', 'Pause background video');
              $('i', this).removeClass('dev-icon-play').addClass('dev-icon-pause');
            }
        });
        */
	}

	function onPlayerStateChange(event){ //this makes sure the video will always play and loop
		if( event.data === 0 ) player.playVideo();
	}

	function csVideoResize(){
		if($("#youtube-video").length) {
        	var topOffSet = $('#gb-page-header').outerHeight() + $('#gb-channel-list-outer').outerHeight();
			var screenWidth = $(window).width();
			var screenHeight = ($(window).height() - topOffSet < 650) ? 650 : $(window).height() - topOffSet; /* Added conditional to act as a min-height for the video. Jeremy K - Case #04000995 */
			var screenRatio = screenHeight / screenWidth;
			var containerHeight = screenWidth * screenRatio;
			var vidWidth = screenWidth, vidHeight = 0;

			// GET ITEMS RATIO
			var initW = 1500, initH = 915;

            if( screenWidth < 768 ){
            	initW = 768;
                initH = 550;
            }

			var ratio = initH / initW;
			vidHeight = vidWidth * ratio;

			if(vidHeight < screenHeight && screenWidth >= 768){
				vidHeight = screenHeight;
				vidWidth = vidHeight / ratio;
			}else if( screenWidth < 768 ){
            	screenHeight = vidHeight;
            }

            var centerWidth = (vidWidth - screenWidth) / 2;
            var centerHeight = (vidHeight - screenHeight) / 2;
			// APPLY THE DIMENSIONS
            $('#hp-slideshow').css({"height": screenHeight + "px"});
			$("#youtube-video").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});
    	}
	}

