link.script:
    $.csHighContrastBar({
    	"show": true,
        "alternateLogo": {
            "selector": "#gb-logo img", // ID OR CLASS OF DEFAULT LOGO 
            "src": "/cms/lib/IL01001530/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", // ALTERNATE LOGO SRC
            "alt": "Peoria Public Schools Logo" // ALTERNATE LOGO ALT TEXT
        },
        "alternateImages": [
        	{
                "selector": ".cs-global-icons > li:nth-child(1) .icon img", // ID OR CLASS OF DEFAULT 
                "src": "Athletics/1.png", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            },
            {
                "selector": ".cs-global-icons > li:nth-child(2) .icon img", // ID OR CLASS OF DEFAULT 
                "src": "Athletics/1.png", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            },
            {
                "selector": ".cs-global-icons > li:nth-child(3) .icon img", // ID OR CLASS OF DEFAULT 
                "src": "Athletics/1.png", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            },
            {
                "selector": ".cs-global-icons > li:nth-child(4) .icon img", // ID OR CLASS OF DEFAULT 
                "src": "Athletics/1.png", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            },
            {
                "selector": ".cs-global-icons > li:nth-child(5) .icon img", // ID OR CLASS OF DEFAULT 
                "src": "Athletics/1.png", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            },
            {
