		"DropdownActions": function(params) {
        	// FOR SCOPE
            var template = this;

            var dropdownParent = params.dropdownParent;
            var dropdownSelector = params.dropdownSelector;
            var dropdown = params.dropdown;
            var dropdownList = params.dropdownList;

            $(dropdownParent + " " + dropdownList + " a").attr("tabindex", "-1");

            // MYSTART DROPDOWN SELECTOR CLICK EVENT
            $(dropdownParent).on("click", dropdownSelector, function(e) {
                e.preventDefault();

                if($(this).parent().hasClass("open")){
                	$("+ " + dropdownList + " a").attr("tabindex", "-1");
                    $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                } else {
                	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden","false").slideDown(300, "swing");
                }
        	});

            // MYSTART DROPDOWN SELECTOR KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownSelector, function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

                        // IF THE DROPDOWN IS OPEN, CLOSE IT
                        if($(dropdownParent).hasClass("open")){
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                            $(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
                        } else {
                        	$(this).attr("aria-expanded", "true").parent().addClass("open").find(dropdown).attr("aria-hidden", "false").slideDown(300, "swing", function(){
                                $(dropdownList + " li:first-child a", this).attr("tabindex", "0").focus();
                            });
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if($("+ " + dropdown + " " + dropdownList + " a").length) {
                        	$("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        	$(this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        }
                    break;

                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.down:
                    case template.KeyCodes.right:
                        e.preventDefault();

                        $("+ " + dropdown + " " + dropdownList + " a").attr("tabindex", "-1");
                        $("+ " + dropdown + " " + dropdownList + " li:first-child > a", this).attr("tabindex", "0").focus();
                    break;
                }
            });

            // MYSTART DROPDOWN LINK KEYDOWN EVENTS
            $(dropdownParent).on("keydown", dropdownList + " li a", function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().prev("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).parent().next("li").find("> a").attr("tabindex", "0").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                    	if(e.shiftKey) {
                        	e.preventDefault();

                        	// FOCUS DROPDOWN BUTTON
                            $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                            $(this).closest(dropdownParent).find(dropdownSelector).focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:first-child > a").attr("tabindex", "0").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(dropdownList).find("a").attr("tabindex", "-1");
                        $(this).closest(dropdownList).find("li:last-child > a").attr("tabindex", "0").focus();
                    break;
                }
            });

            var dropdownTimer;
            $(dropdownParent).mouseenter(function() {
            	clearTimeout(dropdownTimer);
            }).mouseleave(function() {
            	var thisDropdownParent = this;

				dropdownTimer = setTimeout(function(){
                	if($(thisDropdownParent).find("#google_translate_element").length) {
                    	if(!$(thisDropdownParent).find("#cs-branded-translate-label").is(":focus")){
                        	$(dropdownList + " a", this).attr("tabindex", "-1");
                            $(dropdownSelector, this).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                        } else {
                        	$("#cs-branded-translate-label").on("change", function() {
								$(".goog-logo-link").focus();
                            });
                        }
                    } else {
                		$(dropdownList + " a", thisDropdownParent).attr("tabindex", "-1");
						$(dropdownSelector, thisDropdownParent).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing"); 
            		}
                }, 500);
            }).focusout(function() {
                var thisDropdownParent = this;

                setTimeout(function () {
                    if(!$(thisDropdownParent).find(":focus").length && !$(thisDropdownParent).find("#google_translate_element").length) {
                        $(dropdownSelector, thisDropdownParent).attr("aria-expanded", "false").parent().removeClass("open").find(dropdown).attr("aria-hidden", "true").slideUp(300, "swing");
                    }
                }, 500);
            });
        },

		"Header": function() {
            // ADD LOGO
            var logoSrc = jQuery.trim("/cms/lib/IL01001530/Centricity/Template/GlobalAssets/images///logos/logo_horizontal_wtag_C.png");
			var srcSplit = logoSrc.split("/");
			var srcSplitLen = srcSplit.length;
			if((logoSrc != "") && (srcSplit[srcSplitLen - 1] != "default-man.jpg")) {
            	if(!false) {
                	$("#gb-logo").append("
").removeClass("hidden");
                } else {
					$("#gb-logo").append("").removeClass("hidden");
                }
			} else {
            	if(!false) {
                	$("#gb-logo").append("
").removeClass("hidden");
                } else {
					$("#gb-logo").append("").removeClass("hidden");
                }
			}

            var logoWidth = $("#gb-logo").width();
            if(logoWidth > 286) {

            } else {
            	$("#gb-header").css("width", "286px");
            }

            // REMOVE H1 IF SCHOOL NAME TEXT IS BLANK
            if("Peoria Public Schools" == "") {
            	$("#gb-sitename h1.sitename-one").remove();
            }
        },

        "ChannelBar": function() {
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });

            $(".sw-channel-item").each(function(){
            	if($(".sw-channel-dropdown li", this).length == 0) {
                	$(this).addClass("no-dropdown");
                }
            });
        },

        "StickyChannelBar": function() {
			if(!this.IsMyViewPage) {
                switch(this.GetBreakPoint()) {
                    case "desktop":
                        var navOffSet = (this.ChannelBarIsSticky) ? $("nav").offset().top : $("nav").offset().top + $("#gb-channel-list-outer").outerHeight(true);
                        if ($(window).scrollTop() <= navOffSet) {
                            if(this.ChannelBarIsSticky) {
                                $("nav").removeClass("sticky").removeAttr("style");

                                this.ChannelBarIsSticky = false;
                            }
                        } else {
                            $("nav").css("height", $("#gb-channel-list-outer").outerHeight(true));

                            if(!this.ChannelBarIsSticky) {
                                $("nav").addClass("sticky");

                                this.ChannelBarIsSticky = true;
                            }

                            if($(".spn").length || $(".sp").length && $(".ui-widget.app.calendar").length) {
                                if($(".wcm-controls").hasClass("wcm-stuck")) {
                                	$(".wcm-controls").css("margin-top", $("#gb-channel-list-outer").outerHeight(true));
                                }
                        	}
                        }

                        $("#sw-maincontent").css({
                            "display": "block",
                            "position": "relative",
                            "top": "-" + $("#gb-channel-list-outer").outerHeight(true) + "px"
                        });
                    break;
                    case "768": case "640": case "480": case "320":
                        if(this.ChannelBarIsSticky) {
                            $("nav").removeClass("sticky").removeAttr("style");

                            this.ChannelBarIsSticky = false;
                        }
                    break;
                }
            }
        },

        "Body": function() {
        	// FOR SCOPE
            var _this = this;

            // ADD CLASS TO #gb-channel-list
            $("#gb-channel-list").addClass("cs-channel-cont");

            // ADD CLASS TO NAV IF DASHBOARD IS ACTIVE
            if($("#dashboard-sidebar").length) {
                $("nav").addClass("dashboard-active");
            }

            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $(".ui-widget.app .ui-widget-detail img")
                .not($(".ui-widget.app.multimedia-gallery .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // SCROLL DOWN BUTTON
            if($(".hp").length) {
                $(document).on("click keydown", "#gb-scroll-btn", function(e) {
                    if(_this.A11yClick(e)) {
                        e.preventDefault();

                        var scrollPosition = 0;
                        if(!$(this).hasClass("scroll-up")) {
                            scrollPosition = $("#hp-content-outer").offset().top - $("#gb-channel-list-outer").outerHeight(true)
                        }

                        $("html, body").animate({
                            scrollTop: scrollPosition
                        }, "slow");
                    }
                });
            }

            if(!$(".hp-content.one .ui-widget.app").length) {
            	$(".hp-content.one").remove();
            }
            if(!$(".hp-content.two .ui-widget.app").length) {
            	$(".hp-content.two").remove();
            }
            if(!$(".hp-content.three .ui-widget.app").length) {
            	$(".hp-content.three").remove();
            }
            if(!$(".hp-content.four .ui-widget.app").length) {
            	$(".hp-content.four").remove();
            }

            // HOMEPAGE FEATURED HEADLINES
            $(".hp-content.one .headlines, .hp-content.three .headlines").each(function() {
                $(".ui-articles",this).addClass("use-featured");
                $(".ui-articles li:eq(0)",this).wrap("
");
                $(".ui-articles",this).wrapInner("
");
                $(".cs-featured-article",this).prependTo($(".cs-featured-article",this).parent().parent());
            });

            // REGION C
            if(true == true && $(".hp").length) {
            	$(".hp-content.two #sw-content-container2 > div:eq(0)").addClass("first-app");
                var regionCImgSrc = jQuery.trim("/cms/lib/IL01001530/Centricity/Template/GlobalAssets/images///Images/RegionC1023x767.jpg");
                var regionCImgSrcSplit = regionCImgSrc.split("/");
                var regionCImgSrcSplitLen = regionCImgSrcSplit.length;
                if((regionCImgSrc != "") && (regionCImgSrcSplit[regionCImgSrcSplitLen - 1] != "default-man.jpg") && (regionCImgSrcSplit[regionCImgSrcSplitLen - 1] != "regionC-default.png")) {
                	$("
").prependTo(".hp-content.two #sw-content-container2 > div.first-app");
                } else {
                	$("
").prependTo(".hp-content.two #sw-content-container2 > div.first-app");
                }
            }
            $(".hp-content.two #sw-content-container2 > div:not('.first-app') > div").addClass("gb-section");
            $(".hp-content.two #sw-content-container2 > div:eq(0)").next().addClass("padd-fake-region");
            // HEADLINES
            $(".hp-content.two .headlines").each(function() {
                if('true' == false && $(".hp-content.two .first-app .headlines").length) {
                  $(".ui-articles",this).addClass("use-featured");
                  $(".ui-articles li:eq(0)",this).wrap("
");
                  $(".ui-articles",this).wrapInner("
");
                  $(".cs-featured-article",this).prependTo($(".cs-featured-article",this).parent().parent());
                }

                if(!$(this).parent().parent().parent().hasClass("first-app")) {
                  $(".ui-articles",this).addClass("use-featured");
                  $(".ui-articles li:eq(0)",this).wrap("
");
                  $(".ui-articles",this).wrapInner("
");
                  $(".cs-featured-article",this).prependTo($(".cs-featured-article",this).parent().parent());
                }
            });

            // BACKGROUND IMAGE DEFAULT
            var contentBgSrc = jQuery.trim("/cms/lib/IL01001530/Centricity/Template/GlobalAssets/images///Images/Screen Shot 2019-09-24 at 4.46.20 PM.png");
			var contentBgSrcSplit = contentBgSrc.split("/");
			var contentBgSrcSplitLen = contentBgSrcSplit.length;
			if((contentBgSrc != "") && (contentBgSrcSplit[contentBgSrcSplitLen - 1] != "default-man.jpg") && (contentBgSrcSplit[contentBgSrcSplitLen - 1] != "regionEF-bg.jpg")) {
            	$(".hp-content.four, #sp-content-outer, #spn-content-outer").css({
                	"background": "url('" + contentBgSrc + "') no-repeat center center/cover fixed"
                });
            } else {
            	$(".hp-content.four, #sp-content-outer, #spn-content-outer").css({
                	"background": "url('/cms/lib/IL01001530/Centricity/Template/8/defaults/regionEF-bg.jpg') no-repeat center center/cover fixed"
                });
            }

            // MOVE MORE & FOLLOW LINKS INTO APP FOOTER
            $(".ui-widget.app.siteshortcuts").each(function() {
            	var thisShortcuts = this;
                if($(".app-level-social-follow",thisShortcuts).length) {
                	$(thisShortcuts).find(".app-level-social-follow").prependTo($(thisShortcuts).find(".ui-widget-footer"));
                }
            });
            $(".ui-widget.app").each(function() {
            	var thisApp = this;
                if($(".ui-read-more",thisApp).length) {
					$(thisApp).find(".ui-read-more").prependTo($(thisApp).find(".ui-widget-footer"));
                }

                $(".ui-widget-footer .clear",thisApp).remove();
            });

            // SUBPAGE
            // USE CHANNEL NAME FOR PAGE NAV HEADER IF ONE DOESN'T EXIST
            if(!$("div.sp-column.one .ui-widget-header h1").length > 0) {
                $("div.sp-column.one .ui-widget-header").append("
Students & Parents");
            }
        },

        "HomepagePhotoCollage": function() {
        	// PHOTO COLLAGE
            var collageImg1Title = "Personalized Learning";
            var collageImg1Url = "https://www.peoriapublicschools.org/site/Default.aspx?PageID=30315";
            var collageImg2Title = "Get Involved";
            var collageImg2Url = "https://www.peoriapublicschools.org/Page/23235";
            var collageImg3Title = "Social and Emotional Learning";
            var collageImg3Url = "https://www.peoriapublicschools.org/Domain/4940";
            var collageImg4Title = "Sports & Student Activities";
            var collageImg4Url = "https://www.peoriapublicschools.org/Page/32857";
            var collageImg5Title = "Diploma/Degree Program";
            var collageImg5Url = "https://www.peoriapublicschools.org/Domain/5779";
            var collageImg6Title = "Online Academy";
            var collageImg6Url = "https://www.peoriapublicschools.org/Domain/5777";
            var collageImg7Title = "Read Peoria";
            var collageImg7Url = "https://www.peoriapublicschools.org/Domain/6049";
            var collageImg8Title = "Upcoming Events Calendar";
            var collageImg8Url = "www.peoriapublicschools.org/Page/2#calendar";
            var collageImg9Title = "Employment";
            var collageImg9Url = "https://www.peoriapublicschools.org/domain/64";
            var collageImg10Title = "Back to School Information";
            var collageImg10Url = "https://www.peoriapublicschools.org/Domain/15";
            //IMAGE 1
            if(collageImg1Title != "") {
            	$("

").prependTo(".collage-column.one");
                $("" + collageImg1Title + "").prependTo(".collage-column.one .collage-text-cont .collage-text-inner");

            	$(".collage-column.one").hover(function() {
                    $(".collage-text-cont",this).addClass("show-text").attr("aria-hidden","false");
                }, function() {
                	$(".collage-text-cont",this).removeClass("show-text").attr("aria-hidden","true");
                });

                $(".collage-column.one").on("touchstart", function() {
                    $(".collage-text-cont",this).addClass("show-text").attr("aria-hidden","false");
                });

                $(".collage-column.one").on("touchend", function() {
                    $(".collage-text-cont",this).removeClass("show-text").attr("aria-hidden","true");
                });

                $(".collage-column.one .collage-text-cont").focus(function() {
                	$(".collage-column.one").addClass("hover");
                    $(this).addClass("show-text").attr("aria-hidden","false");
                });
                $(".collage-column.one .collage-text-cont").focusout(function() {
