        "Footer": function() {
        	// FOR SCOPE
            var _this = this;

            // SET GOOGLE MAP IFRAME SRC
            var googleMap = "//maps.google.com/maps?q=" + encodeURIComponent("Peoria Public Schools District 150
3202 N. Wisconsin Ave. +Peoria+IL+61603") + "&output=embed&z=12";
            $("#gb-footer-google-map").html('');

            // GOOGLE MAP CLICK
            $(document).on("click keydown", "#gb-footer-google-map", function(e) {
                if(_this.A11yClick(e)) {
                    e.preventDefault();
                    var mapURL = encodeURIComponent("Peoria Public Schools District 150
3202 N. Wisconsin Ave.  Peoria IL 61603");
                    window.location = "//www.google.com/maps/place/" + mapURL;
                }
            });

            if("309-282-3080" == "") {
            	$(".gb-footer-contact.fax").remove();
            }

            if("webmaster@psd150.org" == "") {
            	$(".gb-footer-contact.email").remove();
            }

            // REMOVE FOOTER HEADERS IF NOT IN USE
            if("Find Us" == "") {
            	$("h3.footer-header.contact-info").remove();
            }
            if("Find it Fast" == "") {
            	$("h3.footer-header.extra-links").remove();
            }

            // EXTRA FOOTER LINKS
            $(".gb-footer.two .footer-link > a").each(function() {
            	if($("span",this).text() == "" || $(this).attr("href") == "") {
                	$(this).parent().remove();
                }
            });
            if(!$(".gb-footer.two .footer-link").length) {
            	$(".gb-footer.two").remove();
            }

            // MOVE Bb FOOTER STUFF
            $(".gb-schoolwires-footer.logo").html($("#sw-footer-logo").html());
            var schoolwiresLinks = '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(0)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(2)").html().replace("|", "")) + '';
            schoolwiresLinks += '
' + $.trim($("#sw-footer-links li:eq(1)").html().replace("|", "")) + '';
            $(".gb-schoolwires-footer.links").append('' + schoolwiresLinks + '');
            $(".gb-schoolwires-footer.copyright").append($("#sw-footer-copyright").html());
        },

        "ArticleSlider": function() {
