	var defaultLogoSrc = "/cms/lib/IL01904711/Centricity/Template/21/images/township-logo.png";
	$(window).load(function(){
		searchText();
		calendarListView();
		setupBullets(); //must be called here so that the actually bullets will exist. They get added by a system function sometime on-ready

		//clear logo
		$('.clear-logo').stop(true, true).animate({'padding-left': $('#gb-logo').outerWidth()}, 500);


        if( $('#hp-slideshow-outer .rs-gallery-desc-wrapper').length <= 0 && $('#hp-slideshow-outer .rs-gallery-desc').length > 0 ){
        	$('#hp-slideshow-outer .rs-gallery-desc').wrap('
');
        }

        $('#hp-slideshow-outer .rs-gallery-desc-wrapper').css('height', 'calc(100% - ' + $('#gb-page > header').outerHeight() + 'px)');

			//moves all of the bullets to the left of the links label
		$('.app.navigation .ui-widget-detail > ul li').each(function(k, v){
			$('> .bullet', v).siblings('a').after($('> .bullet', v));
		});

		$('#magic-line').stop().animate({
			left: $('#gb-logo').outerWidth() + 15
		});   

        var $el, $magicChannelLine, leftPos, newWidth,
        $mainNav = $("#sw-channel-list-container");

        $mainNav.append("
");
        $magicChannelLine = $("#channel-magic-line");


        if( $("li.sw-channel-item.active").length > 0 ){
            $magicChannelLine
                .width($("li.sw-channel-item.active").width() - 70)
                .css("left", $("li.sw-channel-item.active").position().left)
                .data("origLeft", $magicChannelLine.position().left)
                .data("origWidth", $magicChannelLine.width());
		}else{
        	$magicChannelLine
                .width(0)
                .css("left", 0)
                .data("origLeft", 0)
                .data("origWidth", 0);
        }

        $("li.sw-channel-item").hover(function() {
            $el = $(this);
            leftPos = $el.position().left;
            newWidth = $el.width();
            $magicChannelLine.stop().animate({
                left: leftPos,
                width: newWidth - 70
            });
        }, function() {
            $magicChannelLine.stop().animate({
                left: $magicChannelLine.data("origLeft"),
                width: $magicChannelLine.data("origWidth")
            });    
        });

        //--> Added by Chris Case 00447255
        if(!($('#hp-content div').length)){
        	$('#hp-content').css('padding', '0');
        }
        //--> End Case 00447255
	});

