//SKELETON
	function pgScreenSize(){
		var getWidth = $(window).width();

        if (getWidth < 1024){ //Mobile BPs
			appView();
		}


		if( getWidth >= 640 ){ //640bp and up
			sameHeights('.sw-channel-item', '.sw-channel-item > a');
			sameHeights('.channel-section', '.channel-section', true, true);
            if( $('#gb-channel-list-outer').hasClass('480moved') ){
            	$('#gb-header').after( $('#gb-channel-list-outer') );
                $('#gb-channel-list-outer').removeClass('480moved');
            }
            if( $('#gb-quick-links-cont').hasClass('mobile-moved') ){
                $('#gb-custom-mystart .inner > div.right').prepend($('#gb-quick-links-cont'));
                $('#gb-quick-links-cont').removeClass('mobile-moved');
            }
		}else{ //480bp and down
            if( !$('#gb-channel-list-outer').hasClass('480moved') ){
            	if( $('#hp-slideshow-outer .rs-gallery-desc-wrapper').length > 0 ){
                	$('#hp-slideshow-outer .rs-gallery-desc-wrapper').before( $('#gb-channel-list-outer') );
                }else{
                    $('#gb-page > main').before( $('#gb-channel-list-outer') );
                }
                $('#gb-channel-list-outer').addClass('480moved');
            }

            if( !$('#gb-quick-links-cont').hasClass('mobile-moved') ){
            	$('#gb-channel-list-outer').before($('#gb-quick-links-cont'));
                $('#gb-quick-links-cont').addClass('mobile-moved');
        	}
        }

		multimediaRotator();

		//clear logo
		$('.clear-logo').stop(true, true).animate({'padding-left': $('#gb-logo').outerWidth()}, 500);

        $('#hp-slideshow-outer .rs-gallery-desc-wrapper').css('height', 'calc(100% - ' + $('#gb-page > header').outerHeight() + 'px)');

		//reposition the magic line
		$('#magic-line').stop().animate({
			left: $('#gb-logo').outerWidth() + 15
		});
	}

	function multimediaRotator(){
		if(!$("#sw-content-container10.region.ui-hp .rs-gallery-container").length && 'true' == 'true' ){

        	$('#hp-video-player-outer').remove();

			// MULTIMEDIA ROTATOR
			$("#sw-content-container10.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 1500,
		                "imageHeight"             : 754,
		                "efficientLoad"           : "yes",
		                "appHeader"               : "no",
		                "titles"                  : "yes",
		                "captions"                : "yes",
		                "galleryOverlay"          : "no",
		                "linkedElement"           : ["image"], // ["image", "title", "overlay", "all"]
		                "playPause"               : "no",
		                "mainControls"            : "no",
