	/*
		heightTarget = the target of the object you want to get heights from
		applyTarget = the target that you want to apply the maxHeight to
	*/
	function sameHeights(heightTarget, applyTarget, matchChannelWidth, verticallyCenterSections){
		if(heightTarget == '.channel-section') $('#mega-school-sections-cont').show();

		var maxHeight = 0;
		$(applyTarget).css('min-height', 0);
		$(heightTarget).each(function(k, v){
			maxHeight = ( ($(v).outerHeight() > maxHeight) ? $(v).outerHeight() : maxHeight );
		});
		$(applyTarget).css('min-height', maxHeight);

		if( matchChannelWidth ){
			$('.sw-channel-item').each(function(k, v){
				$('#mega-school-sections #channel' + (k+1)).css('min-width', $(v).width());
			});
		}

		if( verticallyCenterSections ){
			$('.channel-section').each(function(k, v){
				$('> ul', v).each(function(k1, v1){
					$(v1).css('top', ($(v).outerHeight()/2) - ($(v1).outerHeight()/2));
				});
			});
		}
		if(heightTarget == '.channel-section') $('#mega-school-sections-cont').hide();
	}

	//loads the IFrame Player API code asynchronously.
	var tag = document.createElement('script');
	tag.src = "https://www.youtube.com/iframe_api";
	var firstScriptTag = document.getElementsByTagName('script')[0];
	firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

	//creates an iframe (and YouTube player) after the API code downloads.
	var player;
	var windowWidth;
	var windowHeight;
	function onYouTubeIframeAPIReady() {
		if( !true ){
        	$("#sw-content-container10").remove();
			windowWidth = $(window).width();
			windowHeight = $(window).height();
            if( typeof(YT) != 'undefined' || typeof(YT.Player) != 'undefined' ){
                player = new YT.Player('hp-video-player', {
                    height: windowHeight,
                    width: windowWidth,
                    videoId: 'UVEBci-7LqM',
                    playerVars: { 
                        'modestbranding': 1,
                        'controls': 0, 
                        'autoplay': 1,
                        'rel' : 0,
                        'loop': 1,
                        'wmode': 'opaque'
                    },
                    events: {
                        'onReady': onPlayerReady,
                        'onStateChange': onPlayerStateChange
                    }
                });
        	}
		}else{
			$('#hp-slideshow').removeClass('hidden');
		}
	}

	//the API will call this function when the video player is ready.
	function onPlayerReady(event) {
		csVideoResize();
        player.mute()
		$(window).resize(csVideoResize);

        if( $('#gallery-sidebar .ui-widget.app').length > 0 ){
            $('#gallery-sidebar').appendTo('#hp-video-player-outer .rs-gallery-desc');
        }

        $('#hp-video-player-outer .rs-gallery-desc').prepend('
Pause Video');

        // bind events
        var playButton = document.getElementById("yt-play-button");
        playButton.addEventListener("click", function() {
          if( $(this).hasClass('playing') ){
          	player.pauseVideo();
            $(this).removeClass('playing');
            $('span, i', this).fadeOut(300).promise().done(function(){

                $('#yt-play-button span').html('Play Video');
                $('#yt-play-button i').removeClass('dev-icon-pause').addClass('dev-icon-play');

                $('#yt-play-button span, #yt-play-button i').fadeIn(300);
            });
          }else{
          	player.playVideo();
            $(this).addClass('playing');

            $('span, i', this).fadeOut(300).promise().done(function(){

                $('#yt-play-button span').html('Pause Video');
            	$('#yt-play-button i').removeClass('dev-icon-play').addClass('dev-icon-pause');

                $('#yt-play-button span, #yt-play-button i').fadeIn(300);
            });
          }
        });


        //load up our headline scroller!
        if( $('#sw-content-container12 .app.headlines').length > 0 ){
	        streamingVideoHeadlineRotator();
        }
	}

	function onPlayerStateChange(event){ //this makes sure the video will always play and loop
		if( event.data === 0 ) player.playVideo();
	}

    function csVideoResize(){
		if($("#hp-video-player").length) {
			var screenWidth = $(window).width();
			var screenHeight = $(window).height();
			var screenRatio = screenHeight / screenWidth;
			var containerHeight = screenWidth * screenRatio;
			var vidWidth = screenWidth, vidHeight = 0;

			// GET ITEMS RATIO
			var initW = 1500, initH = 915;

            if( screenWidth < 1024 ){
            	initW = 768;
                initH = 435;
            }

			var ratio = initH / initW;
			vidHeight = vidWidth * ratio;

			if(vidHeight < screenHeight && screenWidth >= 1024){
				vidHeight = screenHeight;
				vidWidth = vidHeight / ratio;
			}else if( screenWidth < 1024 ){
            	screenHeight = vidHeight;
            }

            var centerWidth = (vidWidth - screenWidth) / 2;
            var centerHeight = (vidHeight - screenHeight) / 2;
			// APPLY THE DIMENSIONS
			$("#hp-video-player").css({"width": vidWidth + "px", "height": vidHeight + "px", "margin": "-" + centerHeight + "px 0px 0px -" + centerWidth + "px"});

            $('#hp-slideshow-outer').css('height', screenHeight);
		}
	}

    var streamingHeadlineInterval = {};
	function streamingVideoHeadlineRotator(){
    	$('#sw-content-container12 .ui-articles .custom-read-more').wrap('
'); //get rid of the read mores
        $('#sw-content-container12 .ui-articles .custom-read-more').html(' Learn More');
    	if( $('#sw-content-container12 .ui-articles > li.active').length <= 0 ){ //if we haven't marked an active item mark it now.
        	$('#sw-content-container12 .ui-articles > li').first().addClass('active');
        }
        rotateHeadline('init'); //call a rotation to load up the first one.
    	streamingHeadlineInterval = setInterval(rotateHeadline, 8000); //set an interval to continuously do this.
    }
    function rotateHeadline(isInit){        
    	//fade out the title and caption
        $('.rs-gallery-desc-inner').stop(true, true).fadeOut(400).promise().done(function(){
            //get the next title and put it in place
            $('.rs-gallery-desc .rs-title h1').html($('#sw-content-container12 .ui-articles > li.active .ui-article-title a span').html());
            //get the next caption and put it in place
            $('.rs-gallery-desc .rs-caption p').html($('#sw-content-container12 .ui-articles > li.active .ui-article-description').html());
            //fade in the title and caption
            $('.rs-gallery-desc-inner').stop(true, true).fadeIn(400);

            moveActiveHeadline();
        });
    }

    function moveActiveHeadline(){
    	$('#sw-content-container12 .ui-articles > li.active').addClass('prev-active');
    	//if we are at the end
        if( $('#sw-content-container12 .ui-articles > li').last().hasClass('prev-active') ){
        	//go to the beginning
            $('#sw-content-container12 .ui-articles > li').first().addClass('active');
		}else{
       		//else just go to the next
            $('#sw-content-container12 .ui-articles > li.active').next('li').addClass('active');
        }
        $('#sw-content-container12 .ui-articles > li.prev-active').removeClass('active prev-active');
    }

