    $(function() {
    	CreativeTemplate.Init();
    });

    var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false,
        "WindowWidth": $(window).width(),

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.modMyStart();
            this.modEvents();
            this.Header();
            this.Body();
            this.Footer();
            this.Slideshow(rotatorOptions);
            this.JsMediaQueries();
            this.RsMenu();
            this.Search();

            $(window).load(function(){ _this.WindowLoad(); });
            $(window).resize(function(){ _this.WindowResize(); });

            // DON'T MOVE THIS - ORDER OF OPERATION IS IMPORTANT
            this.AppAccordion();
        },

        "SetTemplateProps": function() {
        	// MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },

        "WindowLoad": function() {

            this.ChannelImage();
            this.ChannelBar();
            this.AccessibilityFeatures();
             $("iframe#player").attr("tabindex","-1");

             var wh = $(window).height();

             if(wh <= 768){

                        var controlsTopMargin = $("#video-bg-wrap .video-overlay-title").height() + $("#video-bg-wrap .video-overlay-description").height() + 26;
                        $("#video-controls").css("margin-top",controlsTopMargin);
                        $("#gb-page").addClass("short-screen");

                    } else {
                    	$("#gb-page").removeClass("short-screen");
                    }
        },

        "WindowResize": function() {
        	if ($(window).width() != this.WindowWidth) {
            	this.WindowWidth = $(window).width();

            	this.JsMediaQueries();
            }
        },

        "JsMediaQueries": function() {
            var template = this;

            switch(this.GetBreakPoint()) {
                case "desktop": 
                    if($("#gb-page.hp").length){
                        if(!$("#dropdown-app .app").length){
                            $("#foreground-left #sw-content-container1").appendTo("#dropdown-app");
                        }
                    }
                    var wh = $(window).height();
                    var hh = $("#gb-header-outer").height();
                    var fh = $("footer").height();
                    $("#hp-slideshow-outer").css("height",wh - (hh+fh));

                    if(wh <= 768){

                        var controlsTopMargin = $("#video-bg-wrap .video-overlay-title").height() + $("#video-bg-wrap .video-overlay-description").height() + 26;
                        $("#video-controls").css("margin-top",controlsTopMargin);
                        $("#gb-page").addClass("short-screen");

                    } else {
                        $("#gb-page").removeClass("short-screen");
                    }

                    //DROPDOWN APP
                    var controlsID = $("#dropdown-app .ui-widget-detail").attr("id");
                    $("#dropdown-app .ui-widget-header").attr({
                        "aria-expanded":"false",
                        "role":"button",
                        "tabindex":"0",
                        "aria-controls":controlsID
                    })

                    $("#dropdown-app .ui-widget-detail").attr("aria-hidden","true");

                    $("#dropdown-app .ui-widget-header").on('click keypress', function(event){
                        if(template.AllyClick(event) === true){
                            //DONT LET THE PAGE JUMP ON KEYPRESS
                            event.preventDefault();

                            if($(this).hasClass("open")){
                                $("#dropdown-app .ui-widget-detail").attr("aria-hidden", "true").slideUp(300, "swing"); 
                                $(this).removeClass("open").attr("aria-expanded","false");

                            } else {
                                if(event.type == "click"){
                                     $("#dropdown-app .ui-widget-detail").attr("aria-hidden","false").slideDown(300, "swing");
                                } else {
                                   $("#dropdown-app .ui-widget-detail").attr("aria-hidden","false").slideDown(300, "swing", function(){
                                        setTimeout(function(){
                                            $("#dropdown-app .ui-widget-detail").find("li:first-child .ui-article-title a").focus();
                                        }, 100);
                                    });

                                }
                                $(this).addClass("open").attr("aria-expanded","true");

                            }

                        }
                    });

                break;
                case "768":
                case "640":
                case "480":
                case "320":
                    if($("#gb-page.hp").length){
                        $("#dropdown-app .ui-widget.app, #foreground-right .ui-widget.app, #foreground-left .ui-widget.app").removeClass("open");

                        if(!$(".foreground-left .app").length){
                            $("#dropdown-app #sw-content-container1").appendTo("#foreground-left");

                            $("#dropdown-app .ui-widget.app, #foreground-right .ui-widget.app, #foreground-left .ui-widget.app").removeClass("open");
                            $("#dropdown-app .ui-widget-header, #foreground-left .ui-widget-header").removeAttr("aria-expanded role tabindex aria-controls");
                            $("#dropdown-app .ui-widget-detail, #foreground-left .ui-widget-detail").removeAttr("aria-hidden");
                        }
                    }
                break;
            }
        },

        "modMyStart": function() {

        	// ADD TRANSLATE
        	$("#mystart-left").creativeTranslate({
                "type": 1,
                "advancedOptions": {
                    "addMethod": "append",
                    "dropdownHandleText": "Language",
                }
            });
            $(".sw-dropdown-selected").remove();
            $(".sw-mystart-dropdown.translate .selector").addClass("district-font").attr({
                "tabindex":"0",
                "aria-expanded":"false",
                "aria-haspopup":"true",
                "aria-label":"Translation Options",
                "role":"button",
                "aria-controls":"ac-translate-list"
            });
            $(".sw-mystart-dropdown.translate .sw-dropdown-list").addClass("ac-dropdown-list").removeClass("sw-dropdown-list");
            $(".sw-mystart-dropdown.translate .sw-dropdown").addClass("ac-dropdown").removeClass("sw-dropdown").attr("aria-hidden","true");
            $(".sw-mystart-dropdown.translate").attr("class","").addClass("ac-dropdown-container translate").removeAttr("tabindex");
            $(".ac-dropdown-container.translate .ac-dropdown-list").attr("id","ac-translate-list");

            //BUILDING MY OWN SCHOOLLIST
	        var acSchoolList = '';

	        $(".sw-mystart-dropdown.schoollist li").each(function(){
	        	var schoolItem = $(this).html();
	            acSchoolList += "
"+schoolItem+"";
	        });  

	        //MOVING ANY MYSTART ELEMENTS TO MATCH DESIGN
	        $(".ac-dropdown-container.school-list .ac-dropdown-list").append(acSchoolList);            
        },

        "Header": function() {
            // FOR SCOPE
            var template = this;

            //COUNT CHANNELS FOR SP IMAGE AND LAST CHANNEL STYLING
            var channelCount = 0;
            $(".sw-channel-item").each(function(){
                $(this).attr("data-image-channel-num",channelCount);
                channelCount++;
            })

            $(".sw-channel-item:last").addClass("last");

            //DROPDOWN APP
            /*var controlsID = $("#dropdown-app .ui-widget-detail").attr("id");
            $("#dropdown-app .ui-widget-header").attr({
                "aria-expanded":"false",
                "role":"button",
                "tabindex":"0",
                "aria-controls":controlsID
            })

            $("#dropdown-app .ui-widget-detail").attr("aria-hidden","true");

            $("#dropdown-app .ui-widget-header").on('click keypress', function(event){
                if(template.AllyClick(event) === true){
                    //DONT LET THE PAGE JUMP ON KEYPRESS
                    event.preventDefault();

                    if($(this).hasClass("open")){
                        $("#dropdown-app .ui-widget-detail").attr("aria-hidden", "true").slideUp(300, "swing"); 
                        $(this).removeClass("open").attr("aria-expanded","false");

                    } else {
                    	if(event.type == "click"){
                             $("#dropdown-app .ui-widget-detail").attr("aria-hidden","false").slideDown(300, "swing");
                        } else {
