        "ChannelImage": function(){
            if($(".sp").length){


                //PLACE CHANNEL IMAGES INTO AN ARRAY

                var bgImage = '';
                var channelBgImages = ['','/cms/lib/IL02000949/Centricity/Template/GlobalAssets/images///images/flex seating 2.jpg', '/cms/lib/IL02000949/Centricity/Template/GlobalAssets/images///images/Channel-Science.jpg', '/cms/lib/IL02000949/Centricity/Template/GlobalAssets/images///images/Channel-book art .jpg', '/cms/lib/IL02000949/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg', '/cms/lib/IL02000949/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg'];
                var defaultImage = "/cms/lib/IL02000949/Centricity/Template/GlobalAssets/images///images/yorkville-sp-default.jpg";

                var activeChannelText = $(".sw-channel-item.active > a span").text();

                if(!$(".sw-channel-item.active").length || activeChannelText == "Home"){
                    $("#channel-image").css("background-image", "url('"+defaultImage+"')");
                } else {
                    //FIND THE ORDER OF THE ACTIVE CHANNEL IN THE CHANNEL BAR
                    var activeChannelOrder = $(".sw-channel-item.active").attr("data-image-channel-num");

                    bgImage = channelBgImages[activeChannelOrder];

                    //CHECK FOR CALENDAR PAGE
                    if(activeChannelText == 'Calendar'){
                        $("#channel-image").css("background-image", "url('"+defaultImage+"')");
                    } else {
                         //CHECK TO SEE IF THERE IS AN IMAGE FOR THE ACTIVE CHANNEL
                        if (bgImage.indexOf("default-man") < 0){
                            $("#channel-image").css("background-image", "url('"+bgImage+"')");

                        } else {
                            if (defaultImage.indexOf("default-man") < 0){
                                $("#channel-image").css("background-image", "url('"+defaultImage+"')");
                            }
                        }
                    }
                } 
            }
        },

        "ChannelBar": function() {

            //BUILD AN ARRAY OF ELEMENTS
            var channelElements = [
                [//CHANNEL ONE
                    true, //CUSTOM DD TOGGLE
                    [//COLUMN ONE CATEGORIES
                        ['About District 115', 10 ],
                        ['',  ],
                        ['',  ],
                        ['', 0 ]
