                    ]
                ],

                [//CHANNEL FOUR
                    true, //CUSTOM DD TOGGLE
                    [//COLUMN ONE CATEGORIES
                        ['E-Learning Plan', 6 ],
                        ['',  ],
                        ['', 0 ],
                        ['', 0 ]
                    ],
                    [//COLUMN TWO CATEGORIES
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ]
                    ],
                    [//COLUMN THREE CATEGORIES
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ]
                    ]
                ],

                [//CHANNEL FIVE
                    true, //CUSTOM DD TOGGLE
                    [//COLUMN ONE CATEGORIES
                        ['COVID-19', 2 ],
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ]
                    ],
                    [//COLUMN TWO CATEGORIES
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ]
                    ],
                    [//COLUMN THREE CATEGORIES
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ],
                        ['', 0 ]
                    ]
                ],

            ];

            //LOOP THROUGH ALL CHANNELS AND LABEL THE ONES USING A MEGA DROPDOWN
            var channelCount = 0;
            $(".sw-channel-item > a[aria-haspopup='true']").each(function(){
            	var megaDropdownToggle = channelElements[channelCount][0];

                if(megaDropdownToggle != false){
                    $(this).parent().addClass("mega-dropdown");
                    $(this).parent().attr("data-mega-channel-num",channelCount);
                }
                channelCount++;
            })

            var numDropdownChannels = $(".sw-channel-item.mega-dropdown").length;

            //LOOP THROUGH CHANNELS THAT HAVE A DROPDOWN
            $(".sw-channel-item.mega-dropdown").each(function(){

                //SETUP A VARIABLE FOR THE NUMBER (out of 5 possible dropdowns) THAT THIS CHANNEL IS      
                var channelNumber = $(this).attr("data-mega-channel-num");

                //SETUP SOME VARIABLES: dropdown html, number of columns that the dropdown has, column html
                var dropdownHTML = "
";
                var numColumns = 0;
                var columnHTML = "
";

                //BEGIN COLUMN LOOP: 
                //LOOP THOUGH EACH COLUMN.
                //NEED TO START COUNTER AT 1 TO SKIP THE TOGGLE ELEMENT IN THE ARRAY
                for (i = 1; i <= 3; i++) {
                    //SETUP A VARIABLE FOR THE NUMBER OF SECTIONS IN A COLUMN
                    var numSections = 0;

					//SETUP A VARIABLE FOR THE NUMBER OF SECTIONS IN THE FIRST CATEGORY WITHIN THE COLUMN
                    //IF IT IS SET TO 0, THIS COLUMN WILL NOT BE USED
                    var categorySections = channelElements[channelNumber][i][0][1];

                    if(categorySections == 0){
                        //DO NOTHING. THIS COLUMN WILL NOT BE USED.. DO NOT COUNT IT TOWARDS THE DROPDOWN COLUMN COUNT
                    } else {
                        //THIS COLUMN HAS AT LEAST 1 HEADING AND SECTIONS
                        //FIND HOW MANY TOTAL SECTIONS WILL BE IN THAT COLUMN
                        for (j = 0; j <= 3; j++) {
                            numSections +=  channelElements[channelNumber][i][j][1];
                        }

                        //NUMBER OF SECTIONS IN EACH CATEGORY WITHIN THAT COLUMN
                        var cat1heading = channelElements[channelNumber][i][0][0];
                        var cat1sections = channelElements[channelNumber][i][0][1];

                        var cat2heading = channelElements[channelNumber][i][1][0];
                        var cat2sections = channelElements[channelNumber][i][1][1];

                        var cat3heading = channelElements[channelNumber][i][2][0];
                        var cat3sections = channelElements[channelNumber][i][2][1];

                        var cat4heading = channelElements[channelNumber][i][3][0];
                        var cat4sections = channelElements[channelNumber][i][3][1];

                        //IF THE FIRST SECTION HAS A HEADING, ADD IT TO THE COLUMN HTML
                        if(cat1heading != ""){
                            columnHTML += "
"+cat1heading+"";
                        }
                        //LOOP THROUGH THE NUMBER OF SECTIONS IN THE FIRST CATEGORY, ADD THEM TO THE COLUMN HTML
                        for(cat1 = 0;cat1 < cat1sections;cat1++){
                            columnHTML += "
"+$(this).find(".sw-channel-dropdown li").eq(cat1).html()+"";
                            $(this).find(".sw-channel-dropdown li").eq(cat1).addClass("tagged");
                        }
                        //REMOVE SECTIONS THAT WERE ADDED SO THE COUNT FOR THE NEXT CATEGORY CAN START AT 0
                        $(".sw-channel-dropdown li.tagged").remove();

						 //IF THE SECOND SECTION HAS A HEADING, ADD IT TO THE COLUMN HTML
                        if(cat2heading != ""){
                            columnHTML += "
"+cat2heading+"";
                        }
                        //LOOP THROUGH THE NUMBER OF SECTIONS IN THE SECOND CATEGORY, ADD THEM TO THE COLUMN HTML
                        for(cat2 = 0;cat2 < cat2sections;cat2++){
                            columnHTML += "
"+$(this).find(".sw-channel-dropdown li").eq(cat2).html()+"";
                            $(this).find(".sw-channel-dropdown li").eq(cat2).addClass("tagged");
                        }
                        //REMOVE SECTIONS THAT WERE ADDED SO THE COUNT FOR THE NEXT CATEGORY CAN START AT 0
                        $(".sw-channel-dropdown li.tagged").remove();

						 //IF THE THIRD SECTION HAS A HEADING, ADD IT TO THE COLUMN HTML
                        if(cat3heading != ""){
                            columnHTML += "
"+cat3heading+"";
                        }
                        //LOOP THROUGH THE NUMBER OF SECTIONS IN THE THIRD CATEGORY, ADD THEM TO THE COLUMN HTML
                        for(cat3 = 0;cat3 < cat3sections;cat3++){
                            columnHTML += "
"+$(this).find(".sw-channel-dropdown li").eq(cat3).html()+"";
                            $(this).find(".sw-channel-dropdown li").eq(cat3).addClass("tagged");
                        }
                        //REMOVE SECTIONS THAT WERE ADDED SO THE COUNT FOR THE NEXT CATEGORY CAN START AT 0
                        $(".sw-channel-dropdown li.tagged").remove();

						//IF THE FOURTH SECTION HAS A HEADING, ADD IT TO THE COLUMN HTML
                        if(cat4heading != ""){
                            columnHTML += "
"+cat4heading+"";
                        }
                        //LOOP THROUGH THE NUMBER OF SECTIONS IN THE FOURTH CATEGORY, ADD THEM TO THE COLUMN HTML
                        for(cat4 = 0;cat4 < cat4sections;cat4++){
                            columnHTML += "
"+$(this).find(".sw-channel-dropdown li").eq(cat4).html()+"";
                            $(this).find(".sw-channel-dropdown li").eq(cat4).addClass("tagged");
                        }
						//REMOVE SECTIONS THAT WERE ADDED SO THE COUNT FOR THE NEXT CATEGORY CAN START AT 0
                        $(".sw-channel-dropdown li.tagged").remove();

                        if(i == 3){ //IN THE THIRD COLUMN
							//CLOSE THE COLUMN
                            columnHTML += "";
                        } else if(i == 2) { //IN THE SECOND COLUMN
                            //CHECK THE FIRST CATEGORY IN THE THIRD COLUMN TO SEE
                            //IF THE THIRD COLUMN WILL BE USED
                            var x = i+1;
                            var nextColumnsNumSections = channelElements[channelNumber][x][0][1];

                            if(nextColumnsNumSections != 0){ //THIRD COLUMN WILL BE USED 
                                //CLOSE THIS COLUMN(2) AND OPEN THE THIRD ONE
                                columnHTML += "
";
                            } else { //THIRD COLUMN WILL NOT BE USED 
                            	//CLOSE THE COLUMN
                                columnHTML += "";
                            }
                        } else if(i == 1){ //IN THE FIRST COLUMN
                            //CHECK THE FIRST CATEGORY IN THE SECOND AND THIRD COLUMNS TO SEE
                            //IF EITHER COLUMN WILL BE USED
                            var x = i+1;
                            var y = i+2;
                            var nextColumnsNumSections = channelElements[channelNumber][x][0][1];
                            var nextNextColumnsNumSections = channelElements[channelNumber][y][0][1];

                            if(nextColumnsNumSections != 0 || nextNextColumnsNumSections != 0){ //THERE WILL BE MORE THAN ONE COLUMN
                                //CLOSE THIS COLUMN(1) AND OPEN THE SECOND ONE
                                columnHTML += "
";
                            } else { //THERE WILL ONLY BE ONE COLUMN
                                //CLOSE THE COLUMN
                                columnHTML += "";
                            }
                        }
						//THIS COLUMN IS IN USE. ADD TO COLUMN COUNT.
                        numColumns++;
                    }
                }

                //ADD COLUMNS HTML TO DROPDOWN AND CLOSE DROPDOWN HTML
                dropdownHTML += columnHTML +"";
                //ADD A CLASS TO THE CHANNEL FOR NUMBER OF COLUMNS
                $(this).addClass("columns-"+numColumns);
                //INSERT NEW DROPDOWN CODE INTO THE PAGE
                $(this).find(".sw-channel-dropdown").html(dropdownHTML);

                //REMOVE CHANNEL COVER - Adam - 3.22.18 - #03107021
                $("#cover-channels").remove();
            })

            $(".sw-channel-item").unbind("hover");
            $(".sw-channel-item").hover(function(){
            	$(".sw-channel-dropdown").hide();
                $(".sw-channel-item ul").stop(true, true);
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){
                $(".sw-channel-dropdown").slideUp(300, "swing");

                $(this).removeClass("hover");
            });
        },

        "modEvents": function(){
        	if (!$(".upcomingevents .joel-month").length) {
		        $(".upcomingevents").modEvents({
		            columns     : "yes",
		            monthLong   : "no",
		            dayWeek     : "yes"
		        });
		        eventsByDay(".upcomingevents .ui-articles");
		    }

		    function eventsByDay(container) {
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };

                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                        $(".sw-calendar-block-time", this).prependTo($(this));
                    });

                    $(".joel-day", this).append(",");
                    $(".joel-month", this).append("");
                });    
			}

        },

        "Body": function() {
            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home").show();

