                    $telerik.$("#ctl01_ctl00_ctl00_FontSizer1_ctl01").click(function() {                                     
                        $telerik.$.cookie("fsdFontSize", null);                        
                        window.location = '/News/ContinuousLearningCOVID19/'; 
                    });

                    // Increases font Size
                    $telerik.$("#ctl01_ctl00_ctl00_FontSizer1_ctl02").click(function() {
                        var currentFontSize = $telerik.$('.sectionContent *').css('font-size');
                        var currentFontSizeNum = parseFloat(currentFontSize, 10);
                        // use styles for tags. {IE6 fix} (because tag font size do not change on IE6)
                        var newFontSize = currentFontSizeNum * 1.2;
                        $telerik.$('.sectionContent *').css({ 'font-size': newFontSize });
                        $telerik.$.cookie("fsdFontSize", null);
                        $telerik.$.cookie("fsdFontSize", newFontSize, { expires: 31 });
                        return true;
                    });

                    // Decreases font Size
                    $telerik.$("#ctl01_ctl00_ctl00_FontSizer1_ctl00").click(function() {
                        var currentFontSize = $telerik.$('.sectionContent *').css('font-size');
                        var currentFontSizeNum = parseFloat(currentFontSize, 10);
                        var newFontSize = currentFontSizeNum * 0.8;
