function __gh__init() {
var delay = 100;
	var maxDelay = 45000;
	var __gh__asyncInit = setInterval(function() {
	    if (typeof $ !== 'undefined' && typeof __gh__webApp !== 'undefined') {
	    	try {
				__gh__webApp.init();
				__gh__webApp.articlePage.init();
				$(function(){try{$.smartbanner()}catch(e){console.warn('smartbanner not initialized',e)}});
			} catch (e) {
				console.error('webapp failed to initialize', e);
			}
	        clearInterval(__gh__asyncInit);
	    } else if (delay > maxDelay) {
	    	console.error('webapp init timed out');
	        clearInterval(__gh__asyncInit);
	    }
	    delay *= 2;
	}, delay);
}
