        var menuIndex = 1;

        // MAIN GLOBAL ICONS
		var iconNum = ("8" == "8;1;2;3;4;5;6;7;0") ? "0" : "8";
		var customIcons = [
                    ["Site Utility/8.png", "Calendar", "/page/2", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
