                          "playPause"                 : showPlayPause,
                          "mainControls"              : "no",
                          "galleryOverlay"            : "no",
                          "linkedElement"             : [], // ["image", "title", "overlay", "all"]
                          "contMoreLink"              : "no",
                          "contMoreLinkText"          : "Read More",
