        OpenDialogOverlay("WindowLargeModal", { LoadType: "U", LoadURL: "https://www.shelby.kyschools.us//site/UserControls/Calendar/CategoryFilterNewWrapper.aspx?DomainID=4&ModuleInstanceID=4490" });
