  // Tab click events
  $("#closings > .tabs > .tab" ).click(function() {

    $("#closings > .tabs > .tab" ).removeClass( "active" );
    $(this).addClass('active');
    $("#closings > .tables_wrap > .tables > .table" ).removeClass( "active" );

    var table = "#"+$(this).data('select');
    $("#closings > .tables_wrap > .tables > "+ table ).addClass('active');

    var cnt = $("#closings > .tables_wrap > .tables > "+table+" > .closing_row").length;
    $("#closings > .table_meta > #table_info").html("About "+ cnt + " "+ $(this).html() + " listings found...");

  });

  // Handle sorting of listing rows
  $(".table_meta > #table_sort").click(function () {
    sortAscending = !sortAscending;

    // Change Sort Info in Table Meta bar
    if (!sortAscending) {
      $(".table_meta > #table_sort").removeClass("table_asc");
      $(".table_meta > #table_sort").addClass("table_desc");
      $("#table_sort_meta").html("Z - A");
    } else {
      $(".table_meta > #table_sort").removeClass("table_desc");
      $(".table_meta > #table_sort").addClass("table_asc");
      $("#table_sort_meta").html("A - Z");
    }

    // Iterate through each of the closings tables
    $("#closings > .tables_wrap > .tables > .table").each(function(key) {
      var closing_table = $(this).attr('id');
      var closing_rows = $("#" + closing_table +" > .closing_row");

      // Grab each row's data-sort value & re-sort
      closing_rows.sort(function (a, b) {
        var sort = a.getAttribute('data-sort') * 1;
        var sort2 = b.getAttribute('data-sort') * 1;

        var mult = 1;
        if (!sortAscending) { mult = -1; }
        if (sort > sort2) { return 1 * mult; }
        if (sort < sort2) { return -1 * mult; }

        return 0;
      });
      closing_rows.detach().appendTo("#"+ closing_table);

    });
  });

