	function pgScreenSize(){
		var getWidth = $(window).width();
		if ( getWidth >= 1024 ) { // 960+
			multimediaRotator();
			sameHeights(["#gb-header > div", ".hp.row.two .column", ".gb.footer", ".upcomingevents .ui-articles > li"]);
			$('.gb.footer.three > a').css('top', 'calc( 50% - ' + (($('.gb.footer.three > a').height() / 2) + 10) + 'px)');
		}else{
			if( getWidth < 640 ){ //run on 480 and below
				appView();
			}

			if(getWidth < 768){ //only run on 640
				$('.gb.footer.one').insertAfter($('.gb.footer.five'));
			}else if( getWidth < 1024 ){ //only run on 768
				$('.gb.footer.one').insertBefore($('.gb.footer.two'));
			}

			if( getWidth > 479 ){ //run on 480 to 768
				multimediaRotator();
				translatorCaption();
			}
		}
		$(".upcomingevents .ui-articles").addClass('ui-clear');
	}

	function multimediaRotator(){
		if(!$("#sw-content-container20.region.ui-hp .rs-gallery-container").length){
			// MULTIMEDIA ROTATOR
			$("#sw-content-container20.ui-hp div.ui-widget.app.cs-rs-multimedia-rotator").csRsMultimediaRotator({
				"imageWidth"              : 1500,
				"imageHeight"             : 915,
				"appHeader"               : "yes",
				"titles"                  : "yes",
				"captions"                : "yes",
				"galleryOverlay"	  : "no",
				"linkedElement"		  : ["title"], // ["image", "title", "overlay", "all"]
				"playPause"               : "no",
				"mainControls"            : "no",
				"contMoreLink"            : "no",
				"contMoreLinkText"        : "Read More",
				"descMoreLink"            : "no",
				"descMoreLinkText"        : "Read More",
				"bullets"                 : "yes",
				"bulletControls"          : "no",
		 		"thumbnails"              : "no",
				"thumbnailWidth"          : 122,
				"thumbnailHeight"         : 53,
				"thumbnailSlideDirection" : "horizontal", // horizontal or vertical
				"hoverPause"              : "yes",
		                "fullScreenRotator"       : "yes", // image dimensions work well at 1500x915
				"fullScreenVideo"         : "no",
				"transitionType"          : "fade", // fade or slide
				"transitionSpeed"         : 3,
				"transitionDelay"         : 8,
				"onImageLoad"             : function() { },
				"allImagesLoaded"         : function() { },
				"onThumbnailLoad"         : function() { },
				"allThumbnailsLoaded"     : function() { },
				"onTransitionStart"       : function() {},
				"onTransitionEnd"         : function() { 
					translatorCaption();
				},
				"watchVidClick"           : function() { },
				"closeVidClick"           : function() { },
				"allLoaded"               : function() { 

					$('#hp-slideshow .rs-gallery-container').append('

');
					$('#hp-slideshow .rs-gallery-desc').appendTo( $("#hp-slideshow .rs-gallery-info-pane > div") );
					$('#hp-slideshow .rs-gallery-bullets-outer').appendTo( $("#hp-slideshow .rs-gallery-info-pane > div") );
					$('#hp-slideshow .ui-widget-header h1').prependTo( $("#hp-slideshow .rs-gallery-info-pane > div") );

                    $('#hp-slideshow .rs-gallery-info-pane').append('SCROLL DOWNfor up-to-date info');

					$('#hp-logo').appendTo( $("#hp-slideshow .rs-gallery-info-pane") );
					translatorCaption();
				}
			});
		}
	}

	function translatorCaption(){
		if( $(window).width() >= 1024 ){
			var middlePos = ($('#hp-slideshow .rs-gallery-info-pane').height()/2) - (parseInt($('#hp-slideshow .rs-gallery-info-pane > div:first-child').height())/2 );
			//$('#hp-slideshow .rs-gallery-info-pane > div:first-child').css({'top': 'calc(50% - ' + middlePos + 'px)', 'bottom': 'auto'}); old way may still be needed
			$('#hp-slideshow .rs-gallery-info-pane > div:first-child').animate({'top': middlePos}).css('bottom', 'auto');
		}else{
			$('#hp-slideshow .rs-gallery-info-pane > div:first-child').css({'bottom': $('#gb-header-outer').outerHeight(), 'top': 'auto'});
		}
	}

	function modEvents(){
	    if (!$(".upcomingevents .joel-month").length) {
	        $(".upcomingevents").modEvents({
	            columns     : "yes",
	            monthLong   : "yes",
	            dayWeek     : "yes"
	        });
	        eventsByDay(".upcomingevents .ui-articles");
	    }
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});

		$(".ui-article", container).each(function(){
			var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
			var dayName = newDateTime[0].split(">");
			if( newDateTime[2].length == 1 ) newDateTime[2] = "0" + newDateTime[2];
			var newDates = '' + newDateTime[2] + '
';
			newDates += newDateTime[1] + '';
			newDates += dayName[0] + '>' + dayName[1].substring(0,3) + '';

			$('.upcoming-column.left h1', this).html(newDates);	

			$('.upcoming-column.right .ui-article-description', this).each(function(){
				if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
					$(this).prepend('ALL DAY');
				}
			});
		});
	}

	function sameHeights(containers){
		$.each(containers, function(k, v){
			maxHeight = 0;
			$(v).css('height', 'auto');
			$(v).each(function(k1, v1){
				if( $(this).height() > maxHeight ) maxHeight = $(this).height();
			});
			$(v).css('height', maxHeight);

			if(!$(v).hasClass('max-height-cont')){
				$(v).addClass('max-height-cont');
			}
		});
	}

	function appView(){
		//DOC dont add app into accordion if it doesnt have a header
		$("#hp-content div.ui-widget.app").each(function() {
			if($(".ui-widget-header", this).hasClass("ui-helper-hidden") || jQuery.trim($(".ui-widget-header h1", this).text()) == "") {
				$(this).addClass("no-header");
			}else if( $("div.ui-widget-header > h1 span.icon-font-2", this).length <= 0 ){
				$("div.ui-widget-header > h1", this).append('');
			}
		});


		//DOC accordion header click
		$("#hp-content div.ui-widget-header").off('click').click(function(){
			var clickedApp = $(this).parent().parent().attr("id");

			if( !$(this).parent().hasClass('active') ){
				$("#hp-content div.ui-widget.app").removeClass("active");
				$(this).parent().addClass("active");
			}else{
				$("#hp-content div.ui-widget.app").removeClass("active");
			}

			smoothScroll("#" + clickedApp, 10);
		});
	}	

