    function sortSchools() {
        $.schoolListMinibase({
            'miniBaseDomainID': 4,
            'miniBaseModuleInstanceID': 11246,
            'cache': true,
            'schoolListReady': function(schoolList) {
                /*
                The returned value is an array of objects and each object is a school record.
                The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                */

                var csElemSchools = [], csSecondarySchools = [], csPreSchools = [], csSpecialSchools = [];

                for(var i in schoolList) {
                    var csSchoolName = schoolList[i].schoolName, csSchoolURL = schoolList[i].schoolUrl, csSchoolType = schoolList[i].schoolType;
                    var csPackedSchool = [csSchoolName, csSchoolURL],
                        csSchoolVisibile = schoolList[i].schoolVisible;

                    //-- ELEMENTARY SCHOOL
                    if(csSchoolType.toLowerCase().indexOf('elementary') > -1 && csSchoolVisibile.toLowerCase() == 'true') {
                        csElemSchools.push(csPackedSchool);
                    }
                    //-- Secondary SCHOOL
                    if(csSchoolType.toLowerCase().indexOf('secondary') > -1 && csSchoolVisibile.toLowerCase() == 'true') {
                        csSecondarySchools.push(csPackedSchool);
                    }
                    //-- Pre SCHOOL
                    if(csSchoolType.toLowerCase().indexOf('signature') > -1 && csSchoolVisibile.toLowerCase() == 'true') { //changed 'pre' to 'signature' by Lyndsay - 10.31.16 - Case 00416696
                        csPreSchools.push(csPackedSchool);
                    }
                    //-- Special SCHOOL
                    if(csSchoolType.toLowerCase().indexOf('special') > -1 && csSchoolVisibile.toLowerCase() == 'true') {
                        csSpecialSchools.push(csPackedSchool);
                    }
                }//End for loop


                function buildSchoolList(csElemSchools, csSecondarySchools, csPreSchools, csSpecialSchools) {
                    // BUILD ELEMENTARY SCHOOL LIST
                    var fullSchoolList = '
 ' + 
                        '
';

                    if(csElemSchools.length > 0){
                        fullSchoolList += '
' +	/*removed class active by Lyndsay */
                            '
' +
                            '';
                        var n = 1;
                        for(var i in csElemSchools) {
                            if(n == 7){
                                fullSchoolList += '
' 
                                    + csElemSchools[i][0] + '';
                                fullSchoolList += '' +
                                    '
' +
                                    '';
                                n = 1;
                            } else {
                                fullSchoolList += '
' 
                                    + csElemSchools[i][0] + '';
                                n++;
                            }
                        }

                        fullSchoolList += '';
                    }
                    if(csSecondarySchools.length > 0){
                        // BUILD Secondary SCHOOL LIST
                        fullSchoolList += '
' +
                                '
' +
                                '';
                        var n = 1;
                        for(var i in csSecondarySchools) {
                            if(n == 7){
                                fullSchoolList += '
' 
                                    + csSecondarySchools[i][0] + '';
                                fullSchoolList += '' +
                                    '
' +
                                    '';
                                n = 1;
                            } else {
                                fullSchoolList += '
' 
                                    + csSecondarySchools[i][0] + '';
                                n++;
                            }
                        }
                        fullSchoolList += '';
                    }
                    if(csPreSchools.length > 0){
                        // BUILD MIDDLE SCHOOL LIST
                        fullSchoolList += '
' +
                                '
' +
                                '';
                        var n = 1;
                        for(var i in csPreSchools) {
                            if(n == 7){
                                fullSchoolList += '
' 
                                    + csPreSchools[i][0] + '';
                                fullSchoolList += '' +
                                    '
' +
                                    '';
                                n = 1;
                            } else {
                                fullSchoolList += '
' 
                                    + csPreSchools[i][0] + '';
                                n++;
                            }
                        }
                        fullSchoolList += '';
                    }
                    if(csSpecialSchools.length > 0){
                        // BUILD HIGH SCHOOL LIST
                        fullSchoolList += '
' +
                                '
' +
                                '';
                        var n = 1;
                        for(var i in csSpecialSchools) {
                            if(n == 7){
                                fullSchoolList += '
' 
                                    + csSpecialSchools[i][0] + '';
                                fullSchoolList += '' +
                                    '
' +
                                    '';
                                n = 1;
                            } else {
                                fullSchoolList += '
' 
                                    + csSpecialSchools[i][0] + '';
                                n++;
                            }
                        }
                        fullSchoolList += '';
                    }
                    fullSchoolList += '' +
                          '
' +
                            '
' +    /*removed class active by Lyndsay*/                  
                                '
' +
                                    'Elementary' +
                                    'ES' +
                                '' +
                            '' +
                            '
' +
                                '
' +
                                    'Secondary' +
                                    'SS' +
                                '' +
                            '' +
                            '
' +
                                '
' +
                                    'Signature' + //changed text from Pre-school by Lyndsay - 10.31.16 - Case 00416696
                                    'PS' +
                                '' +
                            '' +
                            '
' +
                                '
' +
                                    'Special/Optional' +
                                    'Special' +
                                '' +
                            '' +
                        ''
                    '';
                    //-- ADD EVERYTHING
					$('#gb-schoollist-outer').html(fullSchoolList);
       				// schoolListEvents(); commented out by Lyndsay - Case 00388293
                }
                buildSchoolList(csElemSchools, csSecondarySchools, csPreSchools, csSpecialSchools);
				SchoolMenuControls(); //added by Lyndsay - Case 00388293
            }
        });

    }



    //BEGINNING OF LYNDSAY'S ACCESSIBILITY CODE FOR CASE 00388293

    function SchoolMenuControls() {
    	$('#gb-school-list-sections a').attr('tabindex', -1);

        // SCHOOLS BUTTON CLICK
        $("#gb-school-dropdown").click(function(e) {
        	e.preventDefault();
            handleSchoolMenuButtonAction();
        });

        // SCHOOLS KEYDOWN
        $("#gb-school-dropdown").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
                e.preventDefault();
                handleSchoolMenuButtonAction();
            }
        });

        // SCHOOL TAB FOCUS
        $(".gb-school-list-header").focus(function() {
        	// ADJUST TAB ATTRIBUTES
            $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(this).attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");

            // ADJUST PANEL ATTRIBUTES
            $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
            handleTabPanelKeyboardNav($("#gb-schoo-list-sections a"), true);
            $("#" + $(this).attr("aria-controls")).attr({"aria-hidden": "false"}).addClass("active");
            handleTabPanelKeyboardNav($("#" + $(this).attr("aria-controls")));
        });

        // IE TAB CLICK
        $(".ie .gb-school-list-header span").click(function() {
        	$(this).parent().focus();
        });	

        // SCHOOL TAB KEYDOWN
        $(".gb-school-list-header").keydown(function(e) {
        	handleTabListKey($(this), ".gb-school-list-header", e);
        });

        //WHEN PRESSING TAB ON HEADER, FOCUS FIRST SCHOOL LIST ITEM IN PANEL
        $(".gb-school-list-header").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.tab) {
            	e.preventDefault();
                $("#" + $(this).attr("aria-controls") + " > div:first-of-type ul li:first-of-type a").focus();
            }
        });
        // CLOSE MENU WITH ESC
        $(".gb-school-list-header, .gb-schoollist-section").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.esc) {
            	e.preventDefault();

                $("#gb-schoollist-outer").slideUp(function() {
                    // RESET ATTRIBUTES
                    $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                    $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
                }).attr("aria-hidden", "true");

                $("#gb-school-dropdown").attr("aria-expanded", "false").focus();
            }
        });

        // FOCUS LEAVES SCHOOL MENU
        $(document).keydown(function() {
        	// GIVE THE DOCUMENT TIME TO FOCUS THE NEXT ITEM
            setTimeout(function() {
            	// IF THIS DROPDOWN OR ANY OF ITS CHILDREN DO NOT HAVE FOCUS
				if(!$("#gb-schoollist-outer").find(":focus").length && !$("#gb-school-dropdown").is(":focus")) {
                	// CLOSE THE MENU
                    $("#gb-schoollist-outer").slideUp(function() {
                        // RESET ATTRIBUTES
                        $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                        $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
                    }).attr("aria-hidden", "true");

                    $("#gb-school-dropdown").attr("aria-expanded", "false");
                }
            }, 100);
        });
    }

    function handleTabPanelKeyboardNav(tab, clear){
    	if( clear ){
            $('a', tab).off('keydown');
        }else{
            $('a', tab).keydown(function(e){
                var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };
                var linkObj = $('a', tab);

                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                            // IS FIRST ITEM
                        if(linkObj.index($(this)) == 0) {
                            $(".gb-school-list-header.active").focus();
                        } else {
                            linkObj[linkObj.index($(this)) - 1].focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if(linkObj.index($(this)) == (linkObj.length - 1) ) {
                            // FOCUS FIRST ITEM
                            linkObj[0].focus();
                        } else {
                            // FOCUS NEXT ITEM
                            linkObj[linkObj.index($(this)) + 1].focus();
                        }
                    break;

                    case csKeyCodes.tab:
                        e.preventDefault();
                        $('#gb-translate-menu-list > button').focus();

                        $("#gb-schoollist-outer").slideUp(function() {
                            // RESET ATTRIBUTES
                            $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                            $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
                        }).attr("aria-hidden", "true");

                        $("#gb-school-dropdown").attr("aria-expanded", "false");
                    break;

                    default: //pass
                    break;
                }
            });
    	}
    }

    function handleSchoolMenuButtonAction() {
    	// MENU IS NOT OPEN
        if($("#gb-school-dropdown").attr("aria-expanded") == "false") {
            // MAKE SURE ALL ATTRIBUTES ARE RESET
            $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
            $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");

            // SET ELEMENTARY TAB AND PANEL AS ACTIVE
            $(".gb-school-list-header:first").attr({"tabindex": "0", "aria-selected": "true"}).addClass("active");
            $(".gb-school-list-section:first").attr({"aria-hidden": "false"}).addClass("active");

            // OPEN THE MENU
            $("#gb-schoollist-outer").slideDown(function() {
                $(".gb-school-list-header:first").focus();

            }).attr("aria-hidden", "false");

            $("#gb-school-dropdown").attr("aria-expanded", "true");
        }

        // MENU IS OPEN
        else {
            // CLOSE THE MENU
            $("#gb-schoollist-outer").slideUp(function() {
                // RESET ATTRIBUTES
                $(".gb-school-list-header").attr({"tabindex": "-1", "aria-selected": "false"}).removeClass("active");
                $(".gb-school-list-section").attr({"aria-hidden": "true"}).removeClass("active");
            }).attr("aria-hidden", "true");

            $("#gb-school-dropdown").attr("aria-expanded", "false");
        }
    }

    function handleTabListKey(tab, tabClass, event) {
    	var lastIndex = $(tabClass).length - 1;

        switch(event.keyCode){
        	// CONSUME LEFT AND UP ARROWS
            case csKeyCodes.left:
            case csKeyCodes.up:
            	event.preventDefault();

                // FOCUS THE PREVIOUS TAB
                if (tab.index() == 0) { 
                    $(tabClass + ":last-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() - 1).focus();
                } 
            break;

            // CONSUME RIGHT AND DOWN ARROWS
            case csKeyCodes.right:
            case csKeyCodes.down:
            	event.preventDefault();

                // FOCUS THE NEXT TAB
                if (tab.index() == lastIndex) { 
                    $(tabClass + ":first-child").focus();
                } else { 
                    $(tabClass).eq(tab.index() + 1).focus();
                }
            break;

            // CONSUME HOME KEY
            case csKeyCodes.home:
            	event.preventDefault();

                // FOCUS THE FIRST TAB
                $(tabClass + ":first-child").focus();
            break;

            // CONSUME END KEY
            case csKeyCodes.end:
            	event.preventDefault();

                // FOCUS THE LAST TAB
                $(tabClass + ":last-child").focus();
            break;
        }
    }

