	]);

	$(function(){

		// SET IE CLASSES TO BODY
		if(isMSIE){ $("body").addClass("msie"); }
		if(isMSIE8){ $("body").addClass("msie8"); }
		if(isMSIE9){ $("body").addClass("msie9"); }
		if(isMSIE10){ $("body").addClass("msie10"); }

		$(window).bind("resize", function(){ jsMediaQueries(); csVideoResize();stickyCalendarFix(); });

		head.ready("rsMainMenu", function(){
			var additionalHeaders = "", additionalLinks = "";

			// MENU GLOBAL ICONS
			var menuGlobalIcons = [
				["Athletics", "http://neoshor5.schoolwires.net/Domain/12"],
				["Employment", "http://neoshor5.schoolwires.net/Page/1466"],
				["Lunch Menus", "http://neoshor5.schoolwires.net/Page/1476"],
				["Tipline", "http://neoshor5.schoolwires.net/Page/3595"],
				["Calendar", "https://www.neoshosd.org/domain/760"],
				["Directory", "http://neoshor5.schoolwires.net/domain/467"],
				["Parent Resources", "http://neoshor5.schoolwires.net/domain/666"]

			];
			var menuIconNum = ("7" == "7;6;5;4;3;2;1;0") ? 0 : "7";

			for(var i = 0; i < menuIconNum; i++) {
				var linkCheck = (menuGlobalIcons[i][1] == "[SITECALENDARLINK]") ? "/Page/2" : menuGlobalIcons[i][1];
				additionalLinks += "4, " + menuGlobalIcons[i][0] + ", " + linkCheck + ", _self;";
			}

			if(menuIconNum > 0) {
				additionalHeaders = "Popular Links";
			}

			// RESPONSIVE MAIN MENU
			var breakpoint = "";
			if("neoshor5" == "neoshor5") {
				breakpoint = 640;
			} else {
				breakpoint = 768;
			}

			$("body").csRsMenu({
			        "breakPoint"            : breakpoint, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left   
			        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
			        "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#gb-channel-list",
			        "showMenuBtnText"       : "no",
			        "menuBtnText"           : "MENU",
			        "showSchools"           : "no",
			        "showTranslate"         : "yes",
			        "translateId"           : "none",
			        "showAccount"           : "yes",
			        "extraMenuOptions"      : "", // EXTRA MENU HEADING NAMES SEPARATED BY A ;
			        "extraMenuItems"        : "", // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
			        "siteID"                : "4"
			});

		});

		head.ready("rsPagelistMenu", function(){
			// RESPONSIVE PAGELIST MENU
			$("body").csRsPagelistMenu({
			        "breakPoint"            : 640, // SYSTEM BREAK POINTS - 768, 640, 480, 320
			        "slideDirection"        : "left-to-right", // OPTIONS - left-to-right, right-to-left
			        "primaryColor"          : "#2C363E", // DEFAULT - #2C363E
			        "secondaryColor"        : "#222A30", // DEFAULT - #222A30
			        "accentColor"           : "#B5BBBF", // DEFAULT - #B5BBBF
			        "menuButtonParent"      : "#sp-content",
			        "showMenuBtnText"       : "yes",
			        "menuBtnText"           : "PAGES",
			        "siteID"                : "4"
			});
		});	

		head.ready("globalIcons", function() {
			var csIconNum = ("7" == "7;6;5;4;3;2;1;0") ? "0" : "7";
			$("#gb-global-icons").creativeIcons({
			        "iconNum"      : csIconNum, // NUMBER OF ICONS
			        "iconColor"    : "#404040", // ICON COLOR
			        "icon1"        : ["/cms/lib010/MO01909813/Centricity/Template/GlobalAssets/icons/wildcat-logo.png", "Athletics", "http://neoshor5.schoolwires.net/Domain/12", "_self"], // ARRAY ORDER - IMAGE, TEXT, LINK, TARGET
