// SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "BuildStylesheet": function() {
        	var dynStyleSheet = document.createElement('style');

            if(dynStyleSheet) {
                dynStyleSheet.setAttribute('type', 'text/css');
                var head = document.getElementsByTagName('head')[0];

                if(head) {
                    head.insertBefore(dynStyleSheet, head.childNodes[0]);
                }

                var rules = document.createTextNode(this.DynamicStyles);

                if(dynStyleSheet.styleSheet){ // IE
                    dynStyleSheet.styleSheet.cssText = this.DynamicStyles;
                } else {
                    dynStyleSheet.appendChild(rules);
                }
            }
        },


// GETTERS
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },
        "HexToRgb": function(hex){
        	var r = parseInt((this.CutHex(hex)).substring(0,2),16);
	        var g = parseInt((this.CutHex(hex)).substring(2,4),16);
	        var b = parseInt((this.CutHex(hex)).substring(4,6),16);
	        return r+', '+g+', '+b;
        },
        "CutHex": function(hex){
        	return (h.charAt(0)=="#") ? hex.substring(1,7):hex;
        },


//TEMPLATE METHODS
        "MyStart": function() {
        	$('#sw-mystart-search').remove();

        	if($(".sw-mystart-button.signin").length > 0){ //SignIn
                $('#custom-mystart-signin').append($(".sw-mystart-button.signin").html() + "");
                $('#custom-mystart-signin a').addClass("mystart-label");

                if($(".sw-mystart-button.register").length > 0){ //Register
                    $('#custom-mystart-register').append($(".sw-mystart-button.register").html());
                    $('#custom-mystart-register a').addClass("mystart-label");
                }
            }
            if($('.sw-mystart-button.manage').length > 0){ //Site Manager
                $('#gb-user-options ul').append("
" + $('.sw-mystart-button.manage').html() + "");
                $('#user-option-list #ui-btn-sitemanager').attr('id', '');
            }
            if($('#sw-mystart-mypasskey').length > 0){ //Passkey
                $('#gb-social-media').before($('#sw-mystart-mypasskey').addClass('mystart-item').removeClass('sw-mystart-button'));
                $('#ui-btn-mypasskey').addClass('mystart-label');
                $('#ui-btn-mypasskey span').html('Passkeys');
            }
            if( $('#ui-btn-myview').length > 0 ){ //MyView
            	$('#gb-user-options ul').append("
" + $('.sw-mystart-button.pw').html() + "");
                 $('#user-option-list #ui-btn-myview').attr('id', '');
            }

            $('#sw-myaccount-list li').clone().appendTo('#gb-user-options ul');

            $('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#gb-school-dropdown ul');

            this.Translator();
        },
        "Header": function() {
            // ADD LOGO
            var showLogo = true;
            var logoSrc = $.trim("/cms/lib/MO02201556/Centricity/Template/GlobalAssets/images///Logos/normandy-logo.png");
            if(showLogo) {
                var srcSplit = logoSrc.split("/");
                var srcSplitLen = srcSplit.length;
                if((logoSrc == "") || (srcSplit[srcSplitLen - 1] == "default-man.jpg")) {
                   	logoSrc = this.DefaultLogoSrc; //place default logo src here!
                }

                $(".gb-logo .logo-container").prepend('');
            }

            if(true){
            	$('.sitename-container').addClass('text-image').html('');
            }

            this.JsDrawing('header');
        },
        "ChannelBar": function() {
            //Overwrite system hover functionality for an animated version.
            $('#channel-navigation > .sw-channel-item > a').off('touchstart');
            $(".sw-channel-item").unbind('hover');
            $(".sw-channel-item").hover(function(){ //in
                var subList = $(this).children('ul');
                if ($.trim(subList.html()) !== "") {
                    subList.slideDown(300, "swing");
                }
                $(this).addClass("hover");
            }, function(){ //out
                $(".sw-channel-dropdown").slideUp(300, "swing");
                $(this).removeClass("hover");
            });
        },
        "Body": function() {
            // AUTO FOCUS SIGN IN FIELD
            $("#swsignin-txt-username").focus();

            // CENTER SIGNED OUT MESSAGE AND SIGNIN BUTTON
            if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
                $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
                //DOC add signed out breadcrumb
                $(".ui-breadcrumbs").append("
Signed Out");
            }

            // ADJUST SIGN IN PAGE
            if($("#swlogin").length){
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("signin");
                $(".ui-breadcrumbs").append("
Sign In");
            }

            // ADJUST REGISTER PAGE
            if($("#swageprompt-txt-birthmonth").length && $("#swageprompt-txt-birthday").length && $("#swageprompt-txt-birthyear").length) {
                $("#sw-content-layout-wrapper .ui-widget.app").addClass("register");
                $(".ui-breadcrumbs").append("
Register");
            }

            // ADJUST SEARCH RESULTS PAGE
            if($("#sw-content-layout-wrapper.ui-spn #swsearch-pnl-main").length) {
                $("#sw-content-layout-wrapper").children().wrapAll('

');
                $(".ui-breadcrumbs").append("
Search Results");
            }


            // MOVE ALL OF THE APP MORE LINKS TO THE HEADERS
            $("div.ui-widget.app").each(function(appIndex, app){
            	//more link
                if( $('.more-link', app).length > 0 ){
                    $('.ui-widget-header', app)
                    	.addClass('header-buttons-present flex-cont ai-c')
                        .append('
');

                    $('.more-link', app)
                    	.html('SEE MORE NEWS')
                        .appendTo( $('.ui-widget-header-buttons', app) );
                }


                //view calendar link
                if( $('.view-calendar-link', app).length > 0 ){
                	$('.ui-widget-header', app)
                    	.addClass('header-buttons-present flex-cont ai-c')
                        .append('
');

                    $('.view-calendar-link', app)
                    	.html('SEE ALL EVENTS')
                        .appendTo( $('.ui-widget-header-buttons', app) );
                }

                //RSS link
                if( $('.ui-btn-toolbar.rss', app).length > 0 ){
                    if( $('.ui-widget-header-buttons', app).length <= 0 ){
                        $('.ui-widget-header', app)
                        	.addClass('header-buttons-present flex-cont ai-c')
                            .append('
');
                    }

                    $('.ui-btn-toolbar.rss span', app).html('RSS');
                    $('.ui-btn-toolbar.rss', app).appendTo( $('.ui-widget-header-buttons', app) );
                }


                //Hide empty footer
                if( $('.ui-widget-footer', app).children().not('.clear').length <= 0 ){
                	$('.ui-widget-footer', app).addClass('no-children').hide();
                }
            }); 

            // APPLY RESPONSIVE DIMENSIONS TO CONTENT IMAGES
            $("div.ui-widget.app .ui-widget-detail img")
                .not($("div.ui-widget.app.cs-rs-multimedia-rotator .ui-widget-detail img"))
                .not($("div.ui-widget.app.gallery.json .ui-widget-detail img"))
                .each(function() {
                    if ($(this).attr('width') !== undefined && $(this).attr('height') !== undefined) { // IMAGE HAS INLINE DIMENSIONS
                        $(this).css({"display": "inline-block", "width": "100%", "max-width": $(this).attr("width") + "px", "height": "auto", "max-height": $(this).attr("height") + "px"});
                    }
            });

            // ADJUST FIRST BREADCRUMB
            $("li.ui-breadcrumb-first > a > span").text("Home");

            // ADJUST MORE LINKS
            $("a.more-link span", this).text("View All");

            // USE CHANNEL NAME FOR PAGELIST NAV HEADER IF ONE IS NOT PRESENT
            if(!$("div.sp.column.one .ui-widget-header h1").length) {
                $("div.sp.column.one .ui-widget-header").append("
");
            }


            //Customize Apps
            this.UpcomingEvents();
        },
