        "SLM": 	function() {
        	$.schoolListMinibase({
                'miniBaseDomainID': 4,
				'miniBaseModuleInstanceID': 1727,
                'cache': false,
                'schoolListReady': function(schoolList) {
                    /*
                    The returned value is an array of objects and each object is a school record.
                    The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
                    */

                    var schools = '';
                    for(var i in schoolList) {
                        if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                            schools += '
' + schoolList[i].schoolName + '';
                        }
                    }

                    $('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
                    $('.sw-mystart-dropdown.schoollist ul li').clone().appendTo('#school-list');
                    CreativeTemplate.RsMenu(schoolList);
                }
            });
        },
        "RsMenu": function(schoolList) {
        	var mobileSchools = '';

            mobileSchools += '1,District Home,/, _blank;';

            for(var i in schoolList) {
                if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
                    mobileSchools += '1,' + schoolList[i].schoolName + ',' + schoolList[i].schoolUrl + ', _blank;';
                }
            }

            var extraMenuOptions = '';

            var extraMenuItems = '';

            var globalIcons = {
                "Calendar": ["https://www.normandysc.org/Page/2", "_self"],
                "Lunch Menu": ["https://www.normandysc.org/Page/105", "_blank"],
                "Briefcase": ["#", "_self"],
                "Contact Card": ["https://www.normandysc.org/Page/60", "_self"]
            };

            $.each(globalIcons, function(k, v){
                if( k != "" ){
                    extraMenuItems += '2, ' + k + ', ' + v[0] + ', ' + v[1] + ';';
                }
            });

            if( extraMenuItems != "" ) extraMenuOptions = ';Global Links';

            $("body").csRsMenu({
                "breakPoint" : CreativeTemplate.MobileMenuBreakpoint,
                "slideDirection" : "left-to-right",
                "primaryColor" : "#1A1A1A",
                "secondaryColor" : "#0C0C0C",
                "accentColor" : "#BABABA",
                "menuButtonParent" : "#gb-channel-list",
                "showMenuBtnText" : "yes",
                "menuBtnText" : " MENU",
                "showSchools" : "no",
                "showTranslate" : "yes",
                "translateId" : "none",
                "advancedTranlate": {
                    "useAdvancedList": "no",
                },
                "showAccount" : "yes",
                "usePageListNavigation" : "yes",
                "extraMenuOptions"      : "Schools" + extraMenuOptions, // EXTRA MENU HEADING NAMES SEPARATED BY A ;
				"extraMenuItems"        : mobileSchools + extraMenuItems, // EXTRA MENU BUTTONS - STRUCTURE IS: MENU-OPTION-NUMBER, ITEM-NAME, ITEM-URL, ITEM-TARGET;
                "siteID" : "",
                "allLoaded" : function(){ }
            });
        },
