            GetContent("https://www.normandysc.org//site/UserControls/Calendar/CategoryFilterListWrapper.aspx?ModuleInstanceID=" + "1" + "&Expanded=" + true, "calendar-pnl-categoryfilter-open", 2);             
        }
    }

    function ToggleCategoryFilter(expanded) {
        if (expanded === undefined) {
            expanded = $('#calendar-pnl-categoryfilter-closed').is(':visible');
        }

        if (expanded === true) {
            var callback = "$('#calendar-pnl-categoryfilter-closed').fadeOut('fast',function() { $('#calendar-pnl-categoryfilter-open').fadeIn('fast');});";
            GetContent("https://www.normandysc.org//site/UserControls/Calendar/CategoryFilterListWrapper.aspx?ModuleInstanceID=" + "1" + "&Expanded=" + true, "calendar-pnl-categoryfilter-open", 3, callback);
        } else {
             var callback = "$('#calendar-pnl-categoryfilter-open').fadeOut('fast',function() { $('#calendar-pnl-categoryfilter-closed').fadeIn('fast');});";
             GetContent("https://www.normandysc.org//site/UserControls/Calendar/CategoryFilterListWrapper.aspx?ModuleInstanceID=" + "1" + "&Expanded=" + false, "calendar-pnl-categoryfilter-closed", 3, callback); 
         }
     }

