});

$(function(){
	window.setTimeout(function(){
      try {
          var urlData = {};
/*          
          var urlData = {};
*/

          for(var k in urlData)
          {
              var urls = $('a[href*="' + k + '"]');
              urls.each(function(){
                  var o = $(this);
                  var sUrl = o.attr('href');
                  var rx = new RegExp(k + "$", "i");
                  o.attr('href', sUrl.replace(rx, urlData[k]));
              })
          }
      } catch (ex) {};
    }, 10);
});

