                "src": "/cms/lib/NJ01001291/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            },
            {
                "selector": ".cs-global-icons > li:nth-child(7) .icon img", // ID OR CLASS OF DEFAULT 
                "src": "/cms/lib/NJ01001291/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            },
            {
                "selector": ".cs-global-icons > li:nth-child(8) .icon img", // ID OR CLASS OF DEFAULT 
                "src": "/cms/lib/NJ01001291/Centricity/Template/GlobalAssets/images///Faces/default-man.jpg", // ALTERNATE IMAGE SRC
                "alt": "" // ALTERNATE IMAGE ALT TEXT
            }
        ],
        "toggle": function(on) {} // CUSTOM CALLBACK WHEN CONTRAST IS TOGGLED (ALSO FIRES ON DOC READY) - on PARAMETER IS EITHER true OR false (boolean)
    });

    $(function() {
        CreativeTemplate.Init();
    });

    var CreativeTemplate = {
        // PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
        "ShowFullSiteButton": false,
        "ChannelBarIsSticky": false,
        "ShowHighContrast": true,

        // METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.Slideshow();
            this.CheckSlideshow();
            this.MyStart();

            this.Shortcuts();
            this.SchoolList();
            this.Translate();
            this.Header();
            this.Search();
            this.RsMenu();
            this.ChannelBar();
            this.SetChannelDropdownPositions();
            this.StickyChannelBar();
            this.StreamingVideo();
            this.Body();
            this.Homepage();
            this.ModEvents();
            this.GlobalIcons();
            this.AppAccordion();
            this.Footer();
            this.ViewFullSite();
            csGlobalJs.OpenInNewWindowWarning();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });
        },

        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;

            // SCHOOL LIST CHECK
            if($(".sw-mystart-dropdown.schoollist").length) this.ShowSchoolList = true;
        },

        "WindowLoad": function() {
        	this.JsMediaQueries();
            this.HomepageHeadlineSliders();
            this.SetChannelDropdownPositions();

            //SHOW SEARCH
            $("#gb-search").show();

            csGlobalJs.OpenInNewWindowWarning();

            if(this.GetBreakPoint() == "desktop") {
            	var logoWidth = $("#gb-logo-outer").outerWidth();
                $("[data-show-desktop-logo='true'] #gb-sitename-outer").css("margin-left",logoWidth).addClass("initialized");
            }
        },

        "WindowResize": function() {
            this.JsMediaQueries();
            this.HomepageHeadlineSliders();
            this.Shortcuts();
            this.SetChannelDropdownPositions();
            this.CheckSlideshow();
        },

        "WindowScroll": function() {
			this.CheckSlideshow();
        },

        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    //MOVE PASSKEYS
                    if(!$(".header-row.bottom #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").prependTo(".header-row.bottom ");
                    }

                    //SET SITENAME PADDING
                    var logoWidth = $("#gb-logo-outer").outerWidth();
                    $("[data-show-desktop-logo='true'] #gb-sitename-outer").css("margin-left",logoWidth).addClass("initialized");

                    //SET HEADER BOTTOM PADDING FOR FIXED CHANNEL BAR
                    if(true != true){
                    	$("#gb-header-bottom").css("padding-top",$("#gb-header-top").height());
                    }

                    //SET MAP CONTAINER SIZE
                    var footerDifference = $(window).width() - $(".footer-row.contact .gb-section").width();
                    var mapWidth = $(".footer-column.one").width() + (footerDifference / 2);
                    $("#gb-map-container").css("width",mapWidth);
                break;
                case "768":
                    //MOVE PASSKEYS
                    if(!$("#gb-header-top #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").insertAfter("#gb-header-top nav");
                    }
                break;
                case "640":
                    //MOVE PASSKEYS
                    if(!$("#gb-header-top #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").insertAfter("#gb-header-top nav");
                    }
                break;
                case "480":
                    //MOVE PASSKEYS
                    if(!$("#gb-header-top #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").insertAfter("#gb-header-top nav");
                    }
                break;
                case "320":
                    //MOVE PASSKEYS
                    if(!$("#gb-header-top #sw-mystart-mypasskey").length){
                        $("#sw-mystart-mypasskey").insertAfter("#gb-header-top nav");
                    }
                break;
            }
        },

