        "ModEvents": function() {
            // FOR SCOPE
            var template = this;

            $(".ui-widget.app.upcomingevents").modEvents({
                columns     : "yes",
                monthLong   : "no",
                dayWeek     : "yes"
            });

            eventsByDay(".upcomingevents .ui-articles");

            function eventsByDay(container) {
                $(".ui-article", container).each(function(){
                    if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
                        var moveArticle = $(this).html();
                        $(this).parent().prev().children().children().next().append(moveArticle);
                        $(this).parent().remove();
                    };
                }); 

                $(".ui-article", container).each(function(){
                    var newDateTime = $('.upcoming-column.left h1', this).html().toLowerCase().split("");
                    var newDate = '';
                    var dateIndex = ((newDateTime.length > 2) ? 2 : 1); //if the dayWeek is set to yes we need to account for that in our array indexing

                    //if we have the day of the week make sure to include it.
                    if( dateIndex > 1 ){ 
                        newDate += newDateTime[0] + "";
                    }

                    //add in the month
                    newDate += newDateTime[dateIndex - 1] + ''; //the month is always the in the left array position to the date

                    //wrap the date in a new tag
                    newDate += '' + newDateTime[dateIndex] + '';

                    //append the date and month back into their columns
                    $('.upcoming-column.left h1', this).html(newDate);  


                    //add an ALL DAY label if no time was given
                    $('.upcoming-column.right .ui-article-description', this).each(function(){
                        if( $('.sw-calendar-block-time', this).length < 1 ){ //if it doesnt exist add it
                            $(this).prepend('ALL DAY');
                        }
                        $(".sw-calendar-block-time", this).appendTo($(this));
                    });

                    //WRAP DATE AND MONTH IN A CONTAINER
                    $(".jeremy-date, .joel-month", this).wrapAll("");
                }) 
            }

            //ADD NO EVENTS TEXT
            $(".upcomingevents").each(function(){
                if(!$(this).find(".ui-article").length){
                    $("

There are no upcoming events to display.").appendTo($(this).find(".ui-articles"));
                }
            });

            //TABBED EVENTS IN REGIONS B & C
            $("[data-region='b'] .upcomingevents, [data-region='c'] .upcomingevents").each(function(){

                //CREATE TABLIST AND TAB PANELS
                var tabList = "
";
                var tabPanels = "
";
                var uniqueID = $(this).parent().parent().attr("id");
                var dateCounter = 0;

                //LOOP THROUGH EACH EVENT AND PLACE DATE INTO TABLIST AND DETAILS INTO TAB PANELS
                $(this).find(".ui-articles li").each(function(){
                    var eventDate = $(this).find(".adam-hug").html();
                    var eventMonth = $(this).find(".joel-month").text();
                    var eventNumberDay = $(this).find(".jeremy-date").text();
                    var eventDay = $(this).find(".joel-day").html();
                    var eventDetails = $(this).find(".upcoming-column.right").html();
                    var ariaMonth = "";
                    var ariaDate = "";

                    switch(eventMonth){
                        case "jan":
                            ariaMonth = "January";
                        break;
                        case "feb":
                            ariaMonth = "February";
                        break;
                        case "mar":
                            ariaMonth = "March";
                        break;
                        case "apr":
                            ariaMonth = "April";
                        break;
                        case "may":
                            ariaMonth = "May";
                        break;
                        case "jun":
                            ariaMonth = "June";
                        break;
                        case "jul":
                            ariaMonth = "July";
                        break;
                        case "aug":
                            ariaMonth = "August";
                        break;
                        case "sep":
                            ariaMonth = "September";
                        break;
                        case "oct":
                            ariaMonth = "October";
                        break;
                        case "nov":
                            ariaMonth = "November";
                        break;
                        case "dec":
                            ariaMonth = "December";
                        break;
                        default: ariaMonth = eventMonth;
                    }

                    switch(eventNumberDay){
                        case "1":
                            ariaDate = "First";
                        break;
                        case "2":
                            ariaDate = "Second";
                        break;
                        case "3":
                            ariaDate = "Third";
                        break;
                        case "4":
                            ariaDate = "Fourth";
                        break;
                        case "5":
                            ariaDate = "Fifth";
                        break;
                        case "6":
                            ariaDate = "Sixth";
                        break;
                        case "7":
                            ariaDate = "Seventh";
                        break;
                        case "8":
                            ariaDate = "Eighth";
                        break;
                        case "9":
                            ariaDate = "Ninth";
                        break;
                        case "10":
                            ariaDate = "Tenth";
                        break;
                        case "11":
                            ariaDate = "Eleventh";
                        break;
                        case "12":
                            ariaDate = "Twelveth";
                        break;
                        case "13":
                            ariaDate = "Thirteenth";
                        break;
                        case "14":
                            ariaDate = "Fourteenth";
                        break;
                        case "15":
                            ariaDate = "Fifteenth";
                        break;
                        case "16":
                            ariaDate = "Sixteenth";
                        break;
                        case "17":
                            ariaDate = "Seventeenth";
                        break;
                        case "18":
                            ariaDate = "Eighteenth";
                        break;
                        case "19":
                            ariaDate = "Nineteenth";
                        break;
                        case "20":
                            ariaDate = "Twentieth";
                        break;
                        case "21":
                            ariaDate = "Twenty-first";
                        break;
                        case "22":
                            ariaDate = "Twenty-second";
                        break;
                        case "23":
                            ariaDate = "Twenty-third";
                        break;
                        case "24":
                            ariaDate = "Twenty-fourth";
                        break;
                        case "25":
                            ariaDate = "Twenty-fifth";
                        break;
                        case "26":
                            ariaDate = "Twenty-sixth";
                        break;
                        case "27":
                            ariaDate = "Twenty-seventh";
                        break;
                        case "28":
                            ariaDate = "Twenty-eighth";
                        break;
                        case "29":
                            ariaDate = "Twenty-ninth";
                        break;
                        case "30":
                            ariaDate = "Thirtieth";
                        break;
                        case "31":
                            ariaDate = "Thirty-first";
                        break;
                        default: ariaDate = eventNumberDay;
                    }

                    if(dateCounter == 0){//FIRST DATE
                        tabList += "

"+eventDate+"";

                        tabPanels += "

"+eventDay+""+eventDetails+"";
                    } else {//OTHER DATES
                        tabList += "

"+eventDate+"";

                        tabPanels += "

"+eventDay+""+eventDetails+"";
                    }

                    dateCounter++;
                })

                tabList += "";
                tabPanels += "";

                //ADD NEW TABBED EVENT HTML TO THE PAGE
                $("
"+tabList+tabPanels+"").insertAfter($(this).find(".ui-widget-detail"));

                //REMOVE ADDITIONAL EVENTS
                $(".tabbed-events .event-tab:nth-of-type(1n+9)").remove();

                //MOVE VIEW CALENDAR BUTTON
                $(this).find(".view-calendar-link").appendTo($(this).find(".ui-widget-header"));
            })

            //EVENTS TAB NAV
            $(".event-tab").on('focus', function(){
                //CONSTRAIN ACTIONS TO THIS APP
                var appID = $(this).attr("class");
                var appID = appID.split(" ");
                var appID = appID[1];
                var tabNum = $(this).index();

                $(".event-tab."+appID).removeClass("active").attr("tabindex","-1");
                $(this).addClass("active").attr("tabindex","0");

                $(".event-panel."+appID).hide().removeClass("active").attr("aria-hidden","true");
                $(".event-panel."+appID).eq(tabNum).show().addClass("active").attr("aria-hidden","false");
            })

            $(".event-tab").keydown(function(e) {
                //CONSTRAIN ACTIONS TO THIS APP
                var appID = $(this).attr("class");
                var appID = appID.split(" ");
                var appID = appID[1];

                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).is(":first-child")) {
                            // FOCUS DROPDOWN BUTTON
                            $(".event-tab."+appID+":last-child").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).prev().focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();

                        // IS LAST ITEM
                        if($(this).is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(".event-tab."+appID+":first-child").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).next().focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        if(e.shiftKey){
                            e.preventDefault();
                            // IS FIRST ITEM
                            $("#"+appID+" .view-calendar-link").focus();
                        } else {
                            e.preventDefault();

                            $(".event-panel."+appID+".active p:nth-child(2) .sw-calendar-block-title > a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(".event-tab."+appID+":first-child").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(".event-tab."+appID+":last-child").focus();
                    break;
                }
            });

        },

