        $(".sw-mystart-dropdown .selector").removeAttr("aria-hidden").attr({
        	"role": "button",
            "aria-expanded": "false",
            "aria-haspopup": "true",
            "tabindex": "0"
        });

        csGlobalJs.OpenInNewWindowWarning();
	});	

	function jsMediaQueries(){
		// LEFT THIS HERE FOR FUTURE USE IN CASE IT IS NEEDED
		switch(csGetBreakPoint()) {
			case "desktop": 

			break;
			case "768":

			break;
			case "640":

			break;
			case "480":

			break;
			case "320":

			break;
		}
	}

