        },
        "WindowResize": function() {
            this.JsMediaQueries();
            this.CalendarListView();
            this.StickyHeader();
        },
        "StickyHeader": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 
                    var headerHeight = $('#gb-header-outer').height();
                    var channelHeight = $('#gb-channel-list-outer').height();
                    var channelOffset = $("#gb-mystart-outer").height();

                    if($(window).scrollTop() >= headerHeight) {
                      $("nav")
                      	.addClass("fixed")
                        .css('top', channelOffset + 'px');
                      $("main").css("margin-top",channelHeight);

                    } else if($(window).scrollTop() < headerHeight) {
                        $("nav").removeClass("fixed").css('top', '');

                        $("main").css("margin-top","0px");
                    }
                break;
                case "768":
                case "640":
                case "480":
                case "320":
                break;
            }

        },

		//TEMPLATE EVENT HANDLERS
        "JsMediaQueries": function() {
            switch(this.GetBreakPoint()) {
                case "desktop": 

                break;
                case "768":

                break;
                case "640":
                   	$(".hp-content-row-one .content-one-title").click(function(){

                    	if(!$(this).hasClass("closed")){
                        	$(".hp-content-row-one .content-one-caption").slideUp("slow", "swing");
                        	$(".hp-content-row-one ul").slideUp("slow", "swing");
                        	$(this).addClass("closed");
                        } else {
                        	$(".hp-content-row-one .content-one-caption").slideDown("slow", "swing");
                        	$(".hp-content-row-one ul").slideDown("slow", "swing");
                        	$(this).removeClass("closed");
                        }

                    });
                break;
                case "480":
                    $(".hp-content-row-one .content-one-title").click(function(){
                    	if(!$(this).hasClass("closed")){
                        	$(".hp-content-row-one .content-one-caption").slideUp("slow", "swing");
                        	$(".hp-content-row-one ul").slideUp("slow", "swing");
                        	$(this).addClass("closed");
                        } else {
                        	$(".hp-content-row-one .content-one-caption").slideDown("slow", "swing");
                        	$(".hp-content-row-one ul").slideDown("slow", "swing");
                        	$(this).removeClass("closed");
                        }
                    });
                break;
                case "320":
                    $(".hp-content-row-one .content-one-title").click(function(){
                    	if(!$(this).hasClass("closed")){
                        	$(".hp-content-row-one .content-one-caption").slideUp("slow", "swing");
                        	$(".hp-content-row-one ul").slideUp("slow", "swing");
                        	$(this).addClass("closed");
                        } else {
                        	$(".hp-content-row-one .content-one-caption").slideDown("slow", "swing");
                        	$(".hp-content-row-one ul").slideDown("slow", "swing");
                        	$(this).removeClass("closed");
                        }
                    });
                break;
            }
        },

        "AllyClick": function(event) {
            if(event.type == "click") {
                return true;
            } else if(event.type == "keydown" && (event.keyCode == this.KeyCodes.space || event.keyCode == this.KeyCodes.enter)) {
                return true;
            } else {
                return false;
            }
        },

		// INFORMATION SETTERS
        "SetTemplateProps": function() {
            // MYVIEW PAGE CHECK
            if($("#pw-body").length) this.IsMyViewPage = true;
        },
        "GetBreakPoint": function() {
            return window.getComputedStyle(document.querySelector("body"), ":before").getPropertyValue("content").replace(/"|'/g, "");/*"*/
        },

		//TEMPLATE METHODS
        "MyStart": function() {
        	var template = this;

            //TO FIX THE ISSUE OF SEARCH NOT ALLOWING SPACES
            $("#sw-mystart-search").unbind();

            //APPEND SEARCH TO GB-SEARCH-BOTTOM
            $("#sw-mystart-search").appendTo("#gb-search");

            //ADD ROLE AND ARIA LABEL TO SEARCH BUTTON
            $("#sw-search-button").attr("role","button").attr("aria-label","Submit Search");

             $("#gb-search #sw-search-input").attr("tabindex","-1");

             //CLICK FUNCTION FOR THE QUICK LINKS BUTTON
            $("#gb-search-btn").on("click", function(e) {
            	if(template.AllyClick(e)) {
                	if(!$("#gb-search-outer").hasClass("open")){
                    	$("#gb-search-outer").addClass("open").css("height","58px").attr("aria-hidden","false");
                        searchHoverTimer = setTimeout(function(){
                        	$("#gb-search #sw-search-input").focus().attr("tabindex","0");

                        }, 1000);
                    } else {
                    	$(this).attr("aria-expanded","false").focus();
                    	clearTimeout(searchHoverTimer);
                    	$("#gb-search-outer").removeClass("open").css("height","0px").attr("aria-hidden","true");
                        $("#gb-search #sw-search-input").attr("tabindex","-1");
                    }
                }
            });

            //REPLACE MYSTART DROPDOWN ARROWS
            $(".sw-myaccount-chevron").text("");
            $(".sw-dropdown-arrow").text("");

            //ADD TRANSITION CLASS TO ALL MYSTART DROPDOWNS
            $(".sw-mystart-dropdown").addClass("transition");

            //CHANGE THE PASSKEYS TEXT
            $("#ui-btn-mypasskey span").text("Passkeys");

            //ADD TRANSITION CLASS TO ALL MYSTART BUTTONS
            $(".sw-mystart-button").addClass("transition");

            //APPEND MYSTART ITEMS TO APPROPRIATE PLACES


            $('.sw-mystart-button.signin').appendTo("#gb-mystart-inner .gb-mystart.left");
            $('.sw-mystart-button.register').appendTo("#gb-mystart-inner .gb-mystart.left");



            $("#sw-myaccount").removeAttr("tabindex");
            $("#sw-mystart-account").attr("tabindex","0");

            //DOC ADD CUSTOM CLICK EVENT FOR MYACCOUNT DROPDOWN
            $("#sw-mystart-account .sw-dropdown").attr("id","cs-sw-myaccount-list").removeClass("sw-dropdown");
            $("#sw-mystart-account").off();
            $("#sw-mystart-account").click(function(){
                $(".icon-font-arrow",this).toggleClass("open");
                $("#cs-sw-myaccount-list", this).slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list", this).attr("aria-hidden") == "true") ? "false" : "true");
                $(this).toggleClass("open").attr("aria-expanded", ($(this).attr("aria-expanded") == "true") ? "false" : "true");
            });

            $("#gb-mystart #sw-mystart-account").attr({
            						"role": "button",
                                    "aria-expanded": "false",
                                    "aria-haspopup": "true"
            });

            $("#cs-sw-myaccount-list li").each(function(){
                $("> a", this).attr("tabindex", "0"); 
            }); 

            $("#sw-mystart-account").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();

						$(".icon-font-arrow",this).toggleClass("open");
                        $("#cs-sw-myaccount-list",this).slideToggle("slow", "swing").attr("aria-hidden", ($("#cs-sw-myaccount-list").attr("aria-hidden") == "true") ? "false" : "true");


                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                        if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                            $(this).addClass("open").attr("aria-expanded","true");
                            $("#cs-sw-myaccount-list", this).find("li:first-child a").focus();
                        } else {
                            $(this).removeClass("open").attr("aria-expanded","false");
                        }
                        // FOCUS FIRST ITEM

                    break;
                }
            });    

            $("#cs-sw-myaccount-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME TAB KEY
                    case template.KeyCodes.tab:
                        e.preventDefault();

                        $(this).parent().parent().slideUp("slow", "swing").attr("aria-hidden", "true");
                        $(this).parent().parent().parent().attr("aria-expanded","false").removeClass("clicked-state open").focus();

                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest("#cs-sw-myaccount-list").find("li:last-child > a").focus();
                    break;
                }
            });

            //REMOVE DUPLICATE IDs
            $("#sw-mystart-left #ui-btn-myview").remove();
            $("#sw-mystart-right #ui-btn-sitemanager").remove();

            this.Translator();

            $('.sw-mystart-button.home').insertBefore(".gb-mystart.right #gb-schools-btn");
            $('.sw-mystart-button.home a').attr("href","https://www.buffaloschools.org/");

            var _this = this;

            $("#gb-mystart-inner #gb-schools-btn").on("click keydown", function(e) {
                if(_this.AllyClick(e)) {    
                    e.preventDefault();
                    if($(this).hasClass("active")) {
                        $(this).removeClass("active")
                        $("#gb-schoollist-outer").slideUp( "slow" );
                        $(this).focus().attr("aria-expanded", "false");
                    } else {
                        $(this).addClass("active").attr("aria-expanded", "true");
                        $("#gb-schoollist-outer").slideDown( "slow" );
                        $(".gb-school-list-header.elem").focus();
                        $(".gb-school-list-section.elem").addClass("active");
                    }
                }
            });

            $(".sw-mystart-dropdown").each(function(i) {
                // SET ATTRIBUTES
                $(this).attr({
                		"role": "button",
                        "aria-expanded": "false",
                        "aria-haspopup": "true"
                        });
                $(".selector", this).attr("id", "mystart-dropdown-" + (i + 1));
                $(".sw-dropdown-list", this).attr("role", "menu").attr("aria-labelledby", $(".selector", this).attr("id")).attr("aria-hidden","true");
                $(".sw-dropdown", this).attr("aria-hidden", "true");
                $(".sw-dropdown-list li", this).attr("role", "menuitem");
            });

            //DOC ADD CUSTOM CLICK EVENT FOR SCHOOLLIST DROPDOWN
            $(".sw-mystart-dropdown").off();
            $(".sw-mystart-dropdown").click(function() {
                  $(this).toggleClass("open").attr("aria-expanded", ($(this).attr("aria-expanded") == "true") ? "false" : "true");
                  $(".sw-dropdown-arrow",this).toggleClass("open");
                  $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");  
                  $(".sw-dropdown-list", this).attr("aria-hidden", ($(".sw-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 
            });

            $(".sw-mystart-dropdown").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME DOWN ARROW
                    case template.KeyCodes.down:
                    case template.KeyCodes.enter:
                    case template.KeyCodes.space:
                        e.preventDefault();
                        $(".sw-dropdown", this).slideToggle("slow", "swing").attr("aria-hidden", ($(".sw-dropdown", this).attr("aria-hidden") == "true") ? "false" : "true");
                        $(".sw-dropdown-list", this).attr("aria-hidden", ($(".sw-dropdown-list", this).attr("aria-hidden") == "true") ? "false" : "true"); 

                        // CHECK TO MAKE SURE THIS DROPDOWN ISNT ALREADY OPEN
                          if(!$(this).hasClass("open")) {
                              // TOGGLE THE DROPDOWN
                              $(this).addClass("open").attr("aria-expanded","true");
                              $(".sw-dropdown-list li:first-child a", this).focus();

                          } else {
                                $(this).removeClass("open").attr("aria-expanded","false");
                          }
                          $(".sw-dropdown-list li a", this).attr("tabindex","0");
                        // FOCUS FIRST ITEM

                    break;
                }
            });

            $(".sw-mystart-dropdown .sw-dropdown-list li a").keydown(function(e) {
                // CAPTURE KEY CODE
                switch(e.keyCode) {
                    // CONSUME LEFT AND UP ARROWS
                    case template.KeyCodes.left:
                    case template.KeyCodes.up:
                        e.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS LAST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }
                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case template.KeyCodes.right:
                    case template.KeyCodes.down:
                        e.preventDefault();
                        e.stopImmediatePropagation();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS FIRST ITEM
                            $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.tab:
                        e.preventDefault();
                        $(this).parent().parent().parent().parent().attr("aria-expanded", ($(this).parent().parent().parent().parent().attr("aria-expanded") == "true") ? "false" : "true");
                        $(this).parent().parent().parent().slideToggle("slow", "swing").attr("aria-hidden", ($(this).parent().parent().parent().attr("aria-hidden") == "true") ? "false" : "true");
                        $(this).parent().parent().attr("aria-hidden", ($(this).parent().parent().attr("aria-hidden") == "true") ? "false" : "true"); 
                        $(".sw-mystart-dropdown").focus();
                    break;

                    // CONSUME HOME KEY
                    case template.KeyCodes.home:
                        e.preventDefault();

                        // FOCUS FIRST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:first-child > a").focus();
                    break;

                    // CONSUME END KEY
                    case template.KeyCodes.end:
                        e.preventDefault();

                        // FOCUS LAST ITEM
                        $(this).closest(".sw-dropdown-list").find("li:last-child > a").focus();
                    break;
                }
            });

            //DOC ADD CUSTOM CLICK EVENT FOR SCHOOLLIST DROPDOWN
            /*$("#gb-translate-btn").off();
