        "QuickLinks": function() {
        	var template = this;
            var qlStructure = '	';

            var quickLinks = [
                ["Accountability Data","/Page/234","_blank"],
                ["Attendance Data","/Page/4724","_blank"],
                ["District Code of Conduct","/Page/4777","_blank"],
                ["Freedom of Information","/Page/3741","_blank"],
                ["NYSED Data","https://data.nysed.gov/profile.php?instid=800000052968","_blank"],
                ["Office of Shared Accountability","/domain/38","_blank"],
                ["Parent Bill of Rights","/Page/24","_blank"],
                ["Receivership","/Page/85013","_blank"],
                ["(SIG) School Improvement Grants","/Page/85014","_blank"],
                ["Suspension Data","/Page/88331","_blank"]

            ];

            for(var q = 0; q < 10; q++) {
            	if(quickLinks[q][0] != ""){
                	qlStructure += '
' + quickLinks[q][0] + '';

                }    
            } 

            qlStructure += '';
            qlStructure += '';

            $("#gb-quick-links-dropdown").append(qlStructure);

            //SET HEIGHT OF DROPDOWN
            var windowHeight = $(window).height();
            $("#gb-quick-links-dropdown").css("height",windowHeight);
            $(window).resize(function(){
            	var windowHeight = $(window).height();
            	$("#gb-quick-links-dropdown").css("height",windowHeight);
            });

            //CLICK FUNCTION FOR THE QUICK LINKS BUTTON
            $("#gb-quick-links-btn").on("click keydown", function(e) {
            	if(template.AllyClick(e)) {
                	e.preventDefault();
                	if(!$("#gb-quick-links-dropdown").hasClass("open")){
                    	$("#gb-quick-links-dropdown").addClass("open").css("left","0px").attr("aria-hidden","false");
                        $("#gb-quick-links-dropdown li a").attr("tabindex","0");
                        $("#gb-quick-links-dropdown li:first-child a").focus();
                        $("#ql-close-btn").attr("tabindex","0");
                        $(this).attr("aria-expanded","true");
                    } else {
                    	$("#gb-quick-links-dropdown").removeClass("open").css("left","-320px").attr("aria-hidden","true");
                        $("#gb-quick-links-dropdown li a").attr("tabindex","-1");
                        $("#ql-close-btn").attr("tabindex","-1");
                        $(this).focus().attr("aria-expanded","false");
                    }
                }
            });
            $("#ql-close-btn").on("click keydown", function(e) {
            	if(template.AllyClick(e)) {
                    $("#gb-quick-links-dropdown").css("left","-320px").removeClass("open").attr("aria-hidden","true");
                    $("#gb-quick-links-dropdown li a").attr("tabindex","-1");
                    $("#gb-quick-links-btn").focus().attr("aria-expanded","false");
                }
            });

            var csKeyCodes = { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 };

            // SCHOOL TAB KEYDOWN
            $("#gb-quick-links-dropdown li a").keydown(function(e) {

                switch(event.keyCode){
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        event.preventDefault();

                        // IS FIRST ITEM
                        if($(this).parent().is(":first-child")) {
                            // FOCUS CLOSE BUTTON
                            $("#ql-close-btn").focus();
                        } else {
                            // FOCUS PREVIOUS ITEM
                            $(this).parent().prev("li").find("> a").focus();
                        }

                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        event.preventDefault();

                        // IS LAST ITEM
                        if($(this).parent().is(":last-child")) {
                            // FOCUS CLOSE BUTTON
                            $("#ql-close-btn").focus();
                        } else {
                            // FOCUS NEXT ITEM
                            $(this).parent().next("li").find("> a").focus();
                        }  

                    break;

                    case csKeyCodes.tab:
                        event.preventDefault();

                        $("#gb-quick-links-dropdown").removeClass("open").css("left","-320px").attr("aria-hidden","true");
                        $("#gb-quick-links-dropdown li a").attr("tabindex","-1");
                        $("#ql-close-btn").attr("tabindex","-1");
                        $("#gb-quick-links-btn").focus().attr("aria-expanded","false"); 

                    break;
                }

            });

            $("#ql-close-btn").keydown(function(e) {

                switch(event.keyCode){
                    // CONSUME LEFT AND UP ARROWS
                    case csKeyCodes.left:
                    case csKeyCodes.up:
                        event.preventDefault();

                        $("#gb-quick-links-dropdown li:last-child a").focus();

                    break;

                    // CONSUME RIGHT AND DOWN ARROWS
                    case csKeyCodes.right:
                    case csKeyCodes.down:
                        event.preventDefault();

                        $("#gb-quick-links-dropdown li:first-child a").focus();  

                    break;

                    case csKeyCodes.tab:
                        event.preventDefault();

                        $("#gb-quick-links-dropdown").removeClass("open").css("left","-320px").attr("aria-hidden","true");
                        $("#gb-quick-links-dropdown li a").attr("tabindex","-1");
                        $("#ql-close-btn").attr("tabindex","-1");
                        $("#gb-quick-links-btn").focus().attr("aria-expanded","false"); 

                    break;
                }

            });

        },
        "Search": function() {
            //CUSTOM MYSTART SEARCH PLACEHOLDER
            //SEARCH TEXT
            $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
            $("div#sw-mystart-search input#sw-search-input").attr("value", "I'm looking for...");
            $("div#sw-mystart-search input#sw-search-input").focus(function() {
                if($(this).val() == "I'm looking for...") {
                    $(this).val("");
                }
            });
            $("div#sw-mystart-search input#sw-search-input").blur(function() {
                if($(this).val() == "") {
                    $(this).val("I'm looking for...");
                }
            });
        },


