                            setTimeout(function() {
                                $("#hp-slideshow .cs-fullscreen-video-button.mute-button").focus();
                            }, 250);
                        }
                    });

                    setDescPosition();

                    if(_this.GetBreakPoint() != "desktop") {
                        $(".cs-fullscreen-video-buttons").removeClass("video-playing").addClass("video-paused");
                    }
                },
                "onStateChange" : function() { }
            });

