$(document).ready(function(){
   var beaconURL='https://analytics.schoolwires.com/analytics.asmx/Insert?AccountNumber=Uk%2bOl9h8jybqT%2f6w81u%2fIA%3d%3d&SessionID=f1ca7ae9-7b88-4319-92ce-e09e138fbca6&SiteID=8&ChannelID=932&SectionID=933&PageID=4680&HitDate=4%2f22%2f2020+10%3a50%3a02+AM&Browser=Unknown+0.0&OS=Unknown&IPAddress=10.61.22.19';
   try {
      $.getJSON(beaconURL + '&jsonp=?', function(myData) {});
   } catch(err) { 
      // prevent site error for analytics
   }
