link.script:
	    (function () {
	        // if firefox 3.5+, hide content till load (or 3 seconds) to prevent FOUT
	        var d = document,
				e = d.documentElement,
				s = d.createElement('style');
	        if (e.style.MozTransform === '') { // gecko 1.9.1 inference
	            s.textContent = 'body{visibility:hidden}';
	            var r = document.getElementsByTagName('script')[0];
	            r.parentNode.insertBefore(s, r);

	            function f() {
	                s.parentNode && s.parentNode.removeChild(s);
	            }
	            addEventListener('load', f, false);
	            setTimeout(f, 3000);
	        }
	    })();

	    // a nice improvement to this script is to only hide the elements using webfonts
	    // with visibility:hidden instead of the entire 
	    // that's up to you to select them in that textContent line, though

function googleTranslateElementInit() {
    new google.translate.TranslateElement({
        pageLanguage: 'en',
        layout: 0
    }, 'google_translate_element');
}

body.script:
//Initial load of page
$(document).ready(sizeContent);
//Every resize of window
$(window).resize(sizeContent);
//Dynamically assign height
function sizeContent() {
	var newHeight = $(".main-content-wrapper").height();
	$(".side-bar").css("min-height", newHeight + 40);
}

